                }
            }
            drawerLayout.closeDrawers() // fecha o menu depois do clique
            true
        }
    }

    private fun loadProducts() {
        lifecycleScope.launch {
            try {
                // Primeiro vamos tentar criar um produto de teste se não houver nenhum
                createTestProductIfNeeded()

                val result = productRepository.getActiveProducts()

                if (result.isSuccess) {
                    val products = result.getOrNull() ?: emptyList()
                    runOnUiThread {
                        productAdapter.updateProducts(products)

                        if (products.isEmpty()) {
                            Toast.makeText(this@HomeActivity, "Nenhum produto encontrado. Que tal publicar o primeiro?", Toast.LENGTH_LONG).show()
                            // Tentar carregar produtos de exemplo
                            loadSampleProducts()
                        } else {
                            Toast.makeText(this@HomeActivity, "Carregados ${products.size} produtos!", Toast.LENGTH_SHORT).show()
                        }
                    }
                } else {
                    val error = result.exceptionOrNull()
                    val errorMessage = error?.message ?: "Erro desconhecido"
                    runOnUiThread {
                        Toast.makeText(this@HomeActivity, "Erro Firebase: $errorMessage", Toast.LENGTH_LONG).show()

                        // Carregar produtos de exemplo como fallback
                        loadSampleProducts()
                        Toast.makeText(this@HomeActivity, "Carregando produtos de exemplo...", Toast.LENGTH_SHORT).show()
                    }
                }
            } catch (e: Exception) {
                runOnUiThread {
                    Toast.makeText(this@HomeActivity, "Erro de conexão: ${e.message}", Toast.LENGTH_LONG).show()
                    loadSampleProducts()
                    Toast.makeText(this@HomeActivity, "Carregando produtos de exemplo...", Toast.LENGTH_SHORT).show()
                }
            }
        }
    }

    private suspend fun createTestProductIfNeeded() {
        try {
            // Verificar se já existem produtos
            val existingProducts = productRepository.getActiveProducts()
            if (existingProducts.isSuccess && existingProducts.getOrNull()?.isEmpty() == true) {
                // Criar um produto de teste
                val testProduct = Product(
                    name = "Produto Teste",
                    description = "Este é um produto de teste criado automaticamente",
                    price = 99.99,
                    category = "Eletrônicos",
                    stock = 10,
                    imageUrls = emptyList(),
                    active = true
                )

                val saveResult = productRepository.saveProduct(testProduct)
                if (saveResult.isSuccess) {
                    android.util.Log.d("HomeActivity", "Produto de teste criado com sucesso")
                } else {
                    android.util.Log.e("HomeActivity", "Erro ao criar produto de teste: ${saveResult.exceptionOrNull()?.message}")
                }
            }
        } catch (e: Exception) {
            android.util.Log.e("HomeActivity", "Erro ao verificar/criar produto de teste: ${e.message}")
        }
    }

    private fun loadSampleProducts() {
        // Produtos de exemplo caso não consiga carregar do Firebase
        val sampleProducts = listOf(
            Product(
                name = "Notebook Dell",
                description = "Notebook Dell Inspiron",
                price = 3500.0,
                category = "Eletrônicos",
                stock = 5,
                active = true
            ),
            Product(
                name = "Camiseta Azul",
                description = "Camiseta Nike azul",
                price = 79.9,
                category = "Roupas",
                stock = 10,
                active = true
            ),
            Product(
                name = "Livro Kotlin",
                description = "Livro sobre programação Kotlin",
                price = 120.0,
                category = "Livros",
                stock = 3,
                active = true
            )
        )

        productAdapter.updateProducts(sampleProducts)
    }

    override fun onResume() {
        super.onResume()
        // Recarregar produtos quando voltar para a tela
        loadProducts()
    }
}

package com.unasp.unaspmarketplace

import android.os.Bundle
import androidx.appcompat.app.AppCompatActivity
import androidx.recyclerview.widget.GridLayoutManager
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.RecyclerView
import com.unasp.unaspmarketplace.modelos.Category
import com.unasp.unaspmarketplace.modelos.CategoryAdapter
import com.unasp.unaspmarketplace.modelos.ProductAdapter
import android.content.Intent
import androidx.drawerlayout.widget.DrawerLayout
import android.widget.Toast
import androidx.lifecycle.lifecycleScope
import com.google.android.material.navigation.NavigationView
import com.google.android.material.bottomnavigation.BottomNavigationView
import androidx.core.view.GravityCompat
import com.unasp.unaspmarketplace.models.Product
import com.unasp.unaspmarketplace.repository.ProductRepository
import kotlinx.coroutines.launch

class HomeActivity : AppCompatActivity() {
    private lateinit var productRepository: ProductRepository
    private lateinit var productAdapter: ProductAdapter
    private lateinit var recyclerProducts: RecyclerView

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.home_activity)

        // Inicializar repositório
        productRepository = ProductRepository()

        setupCategories()
        setupProducts()
        setupNavigation()

        // Carregar produtos
        loadProducts()
    }

    private fun setupCategories() {
        val categorys = listOf(
            Category("Roupas", R.drawable.tshirt_logo),
            Category("Eletrônicos", R.drawable.computer_logo),
            Category("Alimentos", R.drawable.apple_logo),
            Category("Livros", R.drawable.book_logo)
        )

        val recyclerCategory = findViewById<RecyclerView>(R.id.recyclerCategorys)
        recyclerCategory.layoutManager =
            LinearLayoutManager(this, LinearLayoutManager.HORIZONTAL, false)
        recyclerCategory.adapter = CategoryAdapter(categorys)
    }

    private fun setupProducts() {
        recyclerProducts = findViewById(R.id.recyclerProducts)
        recyclerProducts.layoutManager = GridLayoutManager(this, 2)

        // Inicializar adapter com lista vazia
        productAdapter = ProductAdapter(mutableListOf())
        recyclerProducts.adapter = productAdapter
    }

    private fun setupNavigation() {
        // Configuração do menu lateral
        val drawerLayout = findViewById<DrawerLayout>(R.id.drawerLayout)
        val navigationView = findViewById<NavigationView>(R.id.navigationView)

        // Verificar se deve abrir o menu automaticamente
        if (intent.getBooleanExtra("openMenu", false)) {
            drawerLayout.openDrawer(GravityCompat.START)
        }

        // Configuração da hotbar inferior
        val bottomNavigation = findViewById<BottomNavigationView>(R.id.bottomNavigation)
        bottomNavigation.setOnItemSelectedListener { item ->
            when (item.itemId) {
                R.id.nav_menu -> {
                    // Abre o menu lateral
                    drawerLayout.openDrawer(GravityCompat.START)
                    true
                }
                R.id.nav_home -> {
                    // Já estamos na home, não precisa fazer nada
                    Toast.makeText(this, "Você já está na Home", Toast.LENGTH_SHORT).show()
                    true
                }
                R.id.nav_notifications -> {
                    // Implementar navegação para notificações
                    Toast.makeText(this, "Notificações em breve", Toast.LENGTH_SHORT).show()
                    true
                }
                R.id.nav_cart -> {
                    // Navegar para o carrinho
                    val intent = Intent(this, CartActivity::class.java)
                    startActivity(intent)
                    true
                }
                else -> false
            }
        }

        // Configuração do menu lateral
        navigationView.setNavigationItemSelectedListener { item ->
            when (item.itemId) {
                R.id.nav_post_item -> {
                    val intent = Intent(this, PostItemActivity::class.java)
                    startActivity(intent)
                }
                R.id.nav_profile -> {
                    Toast.makeText(this, "Perfil", Toast.LENGTH_SHORT).show()
                }
            }
            drawerLayout.closeDrawers() // fecha o menu depois do clique
            true
        }
    }

    private fun loadProducts() {
        lifecycleScope.launch {
            try {
                val result = productRepository.getActiveProducts()

                if (result.isSuccess) {
                    val products = result.getOrNull() ?: emptyList()
                    runOnUiThread {
                        productAdapter.updateProducts(products)

                        if (products.isEmpty()) {
                            Toast.makeText(this@HomeActivity, "Nenhum produto encontrado. Que tal publicar o primeiro?", Toast.LENGTH_LONG).show()
                        }
                    }
                } else {
                    val error = result.exceptionOrNull()?.message ?: "Erro desconhecido"
                    runOnUiThread {
                        Toast.makeText(this@HomeActivity, "Erro ao carregar produtos: $error", Toast.LENGTH_LONG).show()

                        // Carregar produtos de exemplo se houver erro
                        loadSampleProducts()
                    }
                }
            } catch (e: Exception) {
                runOnUiThread {
                    Toast.makeText(this@HomeActivity, "Erro de conexão: ${e.message}", Toast.LENGTH_LONG).show()
                    loadSampleProducts()
                }
            }
        }
    }

    private fun loadSampleProducts() {
        // Produtos de exemplo caso não consiga carregar do Firebase
        val sampleProducts = listOf(
            Product(
                name = "Notebook Dell",
                description = "Notebook Dell Inspiron",
                price = 3500.0,
                category = "Eletrônicos",
                stock = 5,
                isActive = true
            ),
            Product(
                name = "Camiseta Azul",
                description = "Camiseta Nike azul",
                price = 79.9,
                category = "Roupas",
                stock = 10,
                isActive = true
            ),
            Product(
                name = "Livro Kotlin",
                description = "Livro sobre programação Kotlin",
                price = 120.0,
                category = "Livros",
                stock = 3,
                isActive = true
            )
        )

        productAdapter.updateProducts(sampleProducts)
    }

    override fun onResume() {
        super.onResume()
        // Recarregar produtos quando voltar para a tela
        loadProducts()
    }
}

package com.unasp.unaspmarketplace

import android.os.Bundle
import android.widget.Toast
import androidx.appcompat.app.AppCompatActivity
import androidx.lifecycle.lifecycleScope
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.RecyclerView
import androidx.swiperefreshlayout.widget.SwipeRefreshLayout
import com.google.firebase.auth.FirebaseAuth
import com.google.firebase.firestore.FirebaseFirestore
import com.google.firebase.firestore.Query
import com.unasp.unaspmarketplace.adapters.OrderHistoryAdapter
import com.unasp.unaspmarketplace.models.Order
import kotlinx.coroutines.launch
import kotlinx.coroutines.tasks.await

class OrderHistoryActivity : AppCompatActivity() {

    private lateinit var recyclerView: RecyclerView
    private lateinit var swipeRefresh: SwipeRefreshLayout
    private lateinit var orderAdapter: OrderHistoryAdapter
    private val ordersList = mutableListOf<Order>()

    private val firestore = FirebaseFirestore.getInstance()
    private val auth = FirebaseAuth.getInstance()

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_order_history)

        setupToolbar()
        setupSwipeRefresh()
        setupRecyclerView()
        loadOrderHistory()
    }

    private fun setupToolbar() {
        val toolbar = findViewById<androidx.appcompat.widget.Toolbar>(R.id.toolbar)
        setSupportActionBar(toolbar)
        supportActionBar?.setDisplayHomeAsUpEnabled(true)
        supportActionBar?.title = "Histórico de Pedidos"
        toolbar.setNavigationOnClickListener { finish() }
    }

    private fun setupSwipeRefresh() {
        swipeRefresh = findViewById(R.id.swipeRefresh)
        swipeRefresh.setOnRefreshListener {
            loadOrderHistory()
        }
    }

    private fun setupRecyclerView() {
        recyclerView = findViewById(R.id.recyclerOrderHistory)
        orderAdapter = OrderHistoryAdapter(ordersList) { order ->
            // Callback para quando um pedido for clicado
            showOrderDetails(order)
        }

        recyclerView.apply {
            layoutManager = LinearLayoutManager(this@OrderHistoryActivity)
            adapter = orderAdapter
        }
    }

    private fun loadOrderHistory() {
        val userId = auth.currentUser?.uid ?: return

        // Mostrar loading apenas se não for refresh
        if (!swipeRefresh.isRefreshing) {
            swipeRefresh.isRefreshing = true
        }

        lifecycleScope.launch {
            try {
                val ordersSnapshot = firestore.collection("orders")
                    .whereEqualTo("userId", userId)
                    .orderBy("timestamp", Query.Direction.DESCENDING)
                    .get()
                    .await()

                ordersList.clear()
                for (document in ordersSnapshot.documents) {
                    val order = document.toObject(Order::class.java)
                    order?.let { ordersList.add(it) }
                }

                orderAdapter.notifyDataSetChanged()

                if (ordersList.isEmpty()) {
                    Toast.makeText(this@OrderHistoryActivity, "Nenhum pedido encontrado", Toast.LENGTH_SHORT).show()
                }

            } catch (e: Exception) {
                Toast.makeText(this@OrderHistoryActivity, "Erro ao carregar pedidos: ${e.message}", Toast.LENGTH_LONG).show()
            } finally {
                swipeRefresh.isRefreshing = false
            }
        }
    }

    private fun showOrderDetails(order: Order) {
        // Aqui você pode abrir uma nova activity ou dialog com detalhes do pedido
        Toast.makeText(this, "Pedido ${order.id} - Total: R$ ${String.format("%.2f", order.getTotalAmount())}", Toast.LENGTH_LONG).show()
    }
}

// Extensão para calcular total do pedido
private fun Order.getTotalAmount(): Double {
    return items.sumOf { it.totalPrice }
}

package com.unasp.unaspmarketplace

import android.content.Intent
import android.os.Bundle
import android.os.CountDownTimer
import android.widget.Button
import android.widget.TextView
import android.widget.Toast
import androidx.appcompat.app.AppCompatActivity
import androidx.lifecycle.lifecycleScope
import com.unasp.unaspmarketplace.models.Order
import com.unasp.unaspmarketplace.models.OrderItem
import com.unasp.unaspmarketplace.utils.CartManager
import com.unasp.unaspmarketplace.utils.WhatsAppHelper
import com.unasp.unaspmarketplace.utils.UserUtils
import kotlinx.coroutines.launch

class OrderPreviewActivity : AppCompatActivity() {

    private lateinit var txtOrderPreview: TextView
    private lateinit var txtCountdown: TextView
    private lateinit var btnSendNow: Button
    private lateinit var btnTestWhatsApp: Button
    private lateinit var btnCancel: Button

    private var countDownTimer: CountDownTimer? = null
    private var order: Order? = null
    private var customerWhatsApp: String? = null

    companion object {
        const val EXTRA_ORDER_ID = "order_id"
        const val EXTRA_CUSTOMER_NAME = "customer_name"
        const val EXTRA_CUSTOMER_WHATSAPP = "customer_whatsapp"
        const val EXTRA_PAYMENT_METHOD = "payment_method"
        const val COUNTDOWN_SECONDS = 8 // 8 segundos para o usuário ler
    }

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.order_preview_activity)

        initViews()
        setupButtons()
        generateOrder()
        startCountdown()
    }

    private fun initViews() {
        txtOrderPreview = findViewById(R.id.txtOrderPreview)
        txtCountdown = findViewById(R.id.txtCountdown)
        btnSendNow = findViewById(R.id.btnSendNow)
        btnTestWhatsApp = findViewById(R.id.btnTestWhatsApp)
        btnCancel = findViewById(R.id.btnCancel)
    }

    private fun setupButtons() {
        btnSendNow.setOnClickListener {
            cancelCountdown()
            sendOrder()
        }

        btnTestWhatsApp.setOnClickListener {
            testWhatsApp()
        }

        btnCancel.setOnClickListener {
            cancelCountdown()
            finish()
        }
    }

    private fun generateOrder() {
        val orderId = intent.getStringExtra(EXTRA_ORDER_ID) ?: return
        val customerName = intent.getStringExtra(EXTRA_CUSTOMER_NAME) ?: return
        val paymentMethod = intent.getStringExtra(EXTRA_PAYMENT_METHOD) ?: return
        customerWhatsApp = intent.getStringExtra(EXTRA_CUSTOMER_WHATSAPP)

        // Obter itens do carrinho
        val cartItems = CartManager.getCartItems()
        if (cartItems.isEmpty()) {
            finish()
            return
        }

        // Converter itens do carrinho para itens do pedido
        val orderItems = cartItems.map { cartItem ->
            OrderItem(
                productName = cartItem.product.name,
                quantity = cartItem.quantity,
                unitPrice = cartItem.product.price,
                totalPrice = cartItem.totalPrice
            )
        }

        // Criar o pedido
        order = Order(
            id = orderId,
            customerName = customerName,
            items = orderItems,
            orderDate = Order.getCurrentDate(),
            paymentMethod = paymentMethod
        )

        // Mostrar preview do pedido
        displayOrderPreview()
    }

    private fun displayOrderPreview() {
        order?.let { order ->
            val orderMessage = formatOrderMessage(order, customerWhatsApp)
            txtOrderPreview.text = orderMessage
        }
    }

    private fun formatOrderMessage(order: Order, customerWhatsApp: String?): String {
        val itemsList = order.items.joinToString("\n") {
            "• ${it.productName} (Qtd: ${it.quantity}) - R$ ${String.format("%.2f", it.totalPrice)}"
        }
        val totalAmount = order.items.sumOf { it.totalPrice }

        val whatsappInfo = if (!customerWhatsApp.isNullOrBlank()) {
            "\n WhatsApp do Cliente: $customerWhatsApp"
        } else ""

        return """
 NOVO PEDIDO - UNASP MARKETPLACE

 ID do Pedido: ${order.id}
 Nome: ${order.customerName}$whatsappInfo
 Local de Retirada: ${order.pickupLocation}
 Data da Compra: ${order.orderDate}
 Forma de Pagamento: ${order.paymentMethod} (na retirada)

️ Itens Comprados:
$itemsList

 Total: R$ ${String.format("%.2f", totalAmount)}

Por favor, confirme o recebimento deste pedido.
        """.trimIndent()
    }

    private fun startCountdown() {
        countDownTimer = object : CountDownTimer((COUNTDOWN_SECONDS * 1000).toLong(), 1000) {
            override fun onTick(millisUntilFinished: Long) {
                val secondsLeft = millisUntilFinished / 1000
                txtCountdown.text = getString(R.string.redirecting_in, secondsLeft.toInt())
            }

            override fun onFinish() {
                sendOrder()
            }
        }.start()
    }

    private fun cancelCountdown() {
        countDownTimer?.cancel()
        countDownTimer = null
    }

    private fun sendOrder() {
        order?.let { order ->
            // Mostrar feedback para o usuário
            Toast.makeText(this, "Abrindo WhatsApp...", Toast.LENGTH_SHORT).show()

            // Salvar dados do usuário se necessário
            saveUserDataIfNeeded(order.customerName, customerWhatsApp)

            // Limpar o carrinho
            CartManager.clearCart()

            // Enviar via WhatsApp
            val orderMessage = formatOrderMessage(order, customerWhatsApp)
            WhatsAppHelper.sendMessage(this, orderMessage)

            // Dar um pequeno delay antes de ir para tela de sucesso
            // para permitir que o WhatsApp abra
            android.os.Handler(android.os.Looper.getMainLooper()).postDelayed({
                goToSuccess(order)
            }, 1500) // 1.5 segundos de delay
        }
    }

    private fun saveUserDataIfNeeded(customerName: String, whatsappNumber: String?) {
        lifecycleScope.launch {
            try {
                val currentUser = UserUtils.getCurrentUser() ?: return@launch

                var shouldUpdate = false
                var updatedUser = currentUser

                // Atualizar nome se estava vazio
                if (currentUser.name.isBlank() && customerName.isNotBlank()) {
                    updatedUser = updatedUser.copy(name = customerName)
                    shouldUpdate = true
                }

                // Atualizar WhatsApp se estava vazio e foi preenchido
                if (currentUser.whatsappNumber.isBlank() && !whatsappNumber.isNullOrBlank()) {
                    updatedUser = updatedUser.copy(whatsappNumber = whatsappNumber)
                    shouldUpdate = true
                }

                if (shouldUpdate) {
                    UserUtils.updateUser(updatedUser)
                }
            } catch (e: Exception) {
                // Se houver erro ao salvar, não interferir no fluxo do pedido
            }
        }
    }

    private fun goToSuccess(order: Order) {
        val intent = Intent(this, OrderSuccessActivity::class.java)
        intent.putExtra("order_id", order.id)
        intent.putExtra("customer_name", order.customerName)
        intent.flags = Intent.FLAG_ACTIVITY_CLEAR_TOP or Intent.FLAG_ACTIVITY_NEW_TASK
        startActivity(intent)
        finish()
    }

    private fun testWhatsApp() {
        val testMessage = " TESTE - UNASP MARKETPLACE\n\nEste é um teste de conectividade.\nSe você recebeu esta mensagem, a integração está funcionando!"

        Toast.makeText(this, "Enviando mensagem de teste...", Toast.LENGTH_SHORT).show()
        WhatsAppHelper.sendMessage(this, testMessage)
    }

    override fun onDestroy() {
        super.onDestroy()
        cancelCountdown()
    }

    override fun onBackPressed() {
        cancelCountdown()
        super.onBackPressed()
    }
}

package com.unasp.unaspmarketplace

import android.content.Intent
import android.os.Bundle
import android.os.CountDownTimer
import android.widget.Button
import android.widget.TextView
import android.widget.Toast
import androidx.appcompat.app.AppCompatActivity
import androidx.lifecycle.lifecycleScope
import com.unasp.unaspmarketplace.models.Order
import com.unasp.unaspmarketplace.models.OrderItem
import com.unasp.unaspmarketplace.utils.CartManager
import com.unasp.unaspmarketplace.utils.WhatsAppHelper
import com.unasp.unaspmarketplace.utils.UserUtils
import kotlinx.coroutines.launch

class OrderPreviewActivity : AppCompatActivity() {

    private lateinit var txtOrderPreview: TextView
    private lateinit var txtCountdown: TextView
    private lateinit var btnSendNow: Button
    private lateinit var btnTestWhatsApp: Button
    private lateinit var btnCancel: Button

    private var countDownTimer: CountDownTimer? = null
    private var order: Order? = null
    private var customerWhatsApp: String? = null

    companion object {
        const val EXTRA_ORDER_ID = "order_id"
        const val EXTRA_CUSTOMER_NAME = "customer_name"
        const val EXTRA_CUSTOMER_WHATSAPP = "customer_whatsapp"
        const val EXTRA_PAYMENT_METHOD = "payment_method"
        const val COUNTDOWN_SECONDS = 8 // 8 segundos para o usuário ler
    }

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.order_preview_activity)

        initViews()
        setupButtons()
        generateOrder()
        startCountdown()
    }

    private fun initViews() {
        txtOrderPreview = findViewById(R.id.txtOrderPreview)
        txtCountdown = findViewById(R.id.txtCountdown)
        btnSendNow = findViewById(R.id.btnSendNow)
        btnTestWhatsApp = findViewById(R.id.btnTestWhatsApp)
        btnCancel = findViewById(R.id.btnCancel)
    }

    private fun setupButtons() {
        btnSendNow.setOnClickListener {
            cancelCountdown()
            sendOrder()
        }

        btnTestWhatsApp.setOnClickListener {
            testWhatsApp()
        }

        btnCancel.setOnClickListener {
            cancelCountdown()
            finish()
        }
    }

    private fun generateOrder() {
        val orderId = intent.getStringExtra(EXTRA_ORDER_ID) ?: return
        val customerName = intent.getStringExtra(EXTRA_CUSTOMER_NAME) ?: return
        val paymentMethod = intent.getStringExtra(EXTRA_PAYMENT_METHOD) ?: return
        customerWhatsApp = intent.getStringExtra(EXTRA_CUSTOMER_WHATSAPP)

        // Obter itens do carrinho
        val cartItems = CartManager.getCartItems()
        if (cartItems.isEmpty()) {
            finish()
            return
        }

        // Converter itens do carrinho para itens do pedido
        val orderItems = cartItems.map { cartItem ->
            OrderItem(
                productId = cartItem.product.id,
                productName = cartItem.product.name,
                quantity = cartItem.quantity,
                unitPrice = cartItem.product.price
            )
        }

        // Criar o pedido
        order = Order(
            id = orderId,
            userId = UserUtils.getCurrentUserId() ?: "",
            customerName = customerName,
            items = orderItems,
            orderDate = Order.getCurrentDate(),
            paymentMethod = paymentMethod,
            totalAmount = orderItems.sumOf { it.totalPrice }
        )

        // Mostrar preview do pedido
        displayOrderPreview()
    }

    private fun displayOrderPreview() {
        order?.let { order ->
            val orderMessage = formatOrderMessage(order, customerWhatsApp)
            txtOrderPreview.text = orderMessage
        }
    }

    private fun formatOrderMessage(order: Order, customerWhatsApp: String?): String {
        val itemsList = order.items.joinToString("\n") {
            "• ${it.productName} (Qtd: ${it.quantity}) - R$ ${String.format("%.2f", it.totalPrice)}"
        }
        val totalAmount = order.items.sumOf { it.totalPrice }

        val whatsappInfo = if (!customerWhatsApp.isNullOrBlank()) {
            "\n WhatsApp do Cliente: $customerWhatsApp"
        } else ""

        return """
 NOVO PEDIDO - UNASP MARKETPLACE

 ID do Pedido: ${order.id}
 Nome: ${order.customerName}$whatsappInfo
 Local de Retirada: ${order.pickupLocation}
 Data da Compra: ${order.orderDate}
 Forma de Pagamento: ${order.paymentMethod} (na retirada)

️ Itens Comprados:
$itemsList

 Total: R$ ${String.format("%.2f", totalAmount)}

Por favor, confirme o recebimento deste pedido.
        """.trimIndent()
    }

    private fun startCountdown() {
        countDownTimer = object : CountDownTimer((COUNTDOWN_SECONDS * 1000).toLong(), 1000) {
            override fun onTick(millisUntilFinished: Long) {
                val secondsLeft = millisUntilFinished / 1000
                txtCountdown.text = getString(R.string.redirecting_in, secondsLeft.toInt())
            }

            override fun onFinish() {
                sendOrder()
            }
        }.start()
    }

    private fun cancelCountdown() {
        countDownTimer?.cancel()
        countDownTimer = null
    }

    private fun sendOrder() {
        order?.let { order ->
            // Mostrar feedback para o usuário
            Toast.makeText(this, "Abrindo WhatsApp...", Toast.LENGTH_SHORT).show()

            // Salvar dados do usuário se necessário
            saveUserDataIfNeeded(order.customerName, customerWhatsApp)

            // Limpar o carrinho
            CartManager.clearCart()

            // Enviar via WhatsApp
            val orderMessage = formatOrderMessage(order, customerWhatsApp)
            WhatsAppHelper.sendMessage(this, orderMessage)

            // Dar um pequeno delay antes de ir para tela de sucesso
            // para permitir que o WhatsApp abra
            android.os.Handler(android.os.Looper.getMainLooper()).postDelayed({
                goToSuccess(order)
            }, 1500) // 1.5 segundos de delay
        }
    }

    private fun saveUserDataIfNeeded(customerName: String, whatsappNumber: String?) {
        lifecycleScope.launch {
            try {
                val currentUser = UserUtils.getCurrentUser() ?: return@launch

                var shouldUpdate = false
                var updatedUser = currentUser

                // Atualizar nome se estava vazio
                if (currentUser.name.isBlank() && customerName.isNotBlank()) {
                    updatedUser = updatedUser.copy(name = customerName)
                    shouldUpdate = true
                }

                // Atualizar WhatsApp se estava vazio e foi preenchido
                if (currentUser.whatsappNumber.isBlank() && !whatsappNumber.isNullOrBlank()) {
                    updatedUser = updatedUser.copy(whatsappNumber = whatsappNumber)
                    shouldUpdate = true
                }

                if (shouldUpdate) {
                    UserUtils.updateUser(updatedUser)
                }
            } catch (e: Exception) {
                // Se houver erro ao salvar, não interferir no fluxo do pedido
            }
        }
    }

    private fun goToSuccess(order: Order) {
        val intent = Intent(this, OrderSuccessActivity::class.java)
        intent.putExtra("order_id", order.id)
        intent.putExtra("customer_name", order.customerName)
        intent.flags = Intent.FLAG_ACTIVITY_CLEAR_TOP or Intent.FLAG_ACTIVITY_NEW_TASK
        startActivity(intent)
        finish()
    }

    private fun testWhatsApp() {
        val testMessage = " TESTE - UNASP MARKETPLACE\n\nEste é um teste de conectividade.\nSe você recebeu esta mensagem, a integração está funcionando!"

        Toast.makeText(this, "Enviando mensagem de teste...", Toast.LENGTH_SHORT).show()
        WhatsAppHelper.sendMessage(this, testMessage)
    }

    override fun onDestroy() {
        super.onDestroy()
        cancelCountdown()
    }

    override fun onBackPressed() {
        cancelCountdown()
        super.onBackPressed()
    }
}

package com.unasp.unaspmarketplace

