import androidx.lifecycle.lifecycleScope
import com.google.android.material.textfield.TextInputEditText
import com.unasp.unaspmarketplace.models.Order
import com.unasp.unaspmarketplace.models.OrderItem
import com.unasp.unaspmarketplace.utils.CartManager
import com.unasp.unaspmarketplace.utils.UserUtils
import kotlinx.coroutines.launch

class PaymentActivity : AppCompatActivity() {

    private lateinit var edtCustomerName: TextInputEditText
    private lateinit var edtWhatsappNumber: TextInputEditText
    private lateinit var rgPaymentMethods: RadioGroup
    private lateinit var rbDebit: RadioButton
    private lateinit var rbCredit: RadioButton
    private lateinit var rbPix: RadioButton
    private lateinit var rbCash: RadioButton
    private lateinit var txtTotal: TextView
    private lateinit var btnConfirmPayment: Button

    private var totalAmount: Double = 0.0

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.payment_activity)

        // Receber o total do carrinho
        totalAmount = intent.getDoubleExtra("totalAmount", 0.0)

        initViews()
        setupButtons()
        updateTotal()
        setupPaymentSelection()
        loadUserData()
    }

    private fun initViews() {
        edtCustomerName = findViewById(R.id.edtCustomerName)
        edtWhatsappNumber = findViewById(R.id.edtWhatsappNumber)
        rgPaymentMethods = findViewById(R.id.rgPaymentMethods)
        rbDebit = findViewById(R.id.rbDebit)
        rbCredit = findViewById(R.id.rbCredit)
        rbPix = findViewById(R.id.rbPix)
        rbCash = findViewById(R.id.rbCash)
        txtTotal = findViewById(R.id.txtTotal)
        btnConfirmPayment = findViewById(R.id.btnConfirmPayment)

        val btnBack = findViewById<ImageView>(R.id.btnBack)
        btnBack.setOnClickListener { finish() }
    }

    private fun loadUserData() {
        lifecycleScope.launch {
            try {
                val currentUser = UserUtils.getCurrentUser()
                currentUser?.let { user ->
                    // Preencher nome se estiver disponível
                    if (user.name.isNotBlank()) {
                        edtCustomerName.setText(user.name)
                    }

                    // Preencher WhatsApp se estiver disponível
                    if (user.whatsappNumber.isNotBlank()) {
                        edtWhatsappNumber.setText(user.whatsappNumber)
                    }

                    // Atualizar estado do botão após carregar dados
                    updateButtonState()
                }
            } catch (e: Exception) {
                // Se houver erro ao carregar dados do usuário, não fazer nada
                // O usuário pode preencher manualmente
            }
        }
    }

    private fun setupButtons() {
        btnConfirmPayment.setOnClickListener {
            generateOrder()
        }
    }

    private fun setupPaymentSelection() {
        rgPaymentMethods.setOnCheckedChangeListener { _, _ ->
            updateButtonState()
        }

        // Listeners para os campos de texto
        edtCustomerName.setOnFocusChangeListener { _, _ ->
            updateButtonState()
        }

        edtWhatsappNumber.setOnFocusChangeListener { _, _ ->
            updateButtonState()
        }
    }

    private fun updateButtonState() {
        val hasName = edtCustomerName.text?.toString()?.trim()?.isNotEmpty() == true
        val hasPaymentMethod = rgPaymentMethods.checkedRadioButtonId != -1

        btnConfirmPayment.isEnabled = hasName && hasPaymentMethod
        btnConfirmPayment.alpha = if (hasName && hasPaymentMethod) 1.0f else 0.5f
    }

    private fun updateTotal() {
        txtTotal.text = "Total: R$ %.2f".format(totalAmount)
    }

    private fun generateOrder() {
        val customerName = edtCustomerName.text?.toString()?.trim()
        val whatsappNumber = edtWhatsappNumber.text?.toString()?.trim()
        val selectedPaymentMethod = getSelectedPaymentMethod()

        // Validações
        if (customerName.isNullOrEmpty()) {
            Toast.makeText(this, "Digite seu nome completo", Toast.LENGTH_SHORT).show()
            return
        }

        if (selectedPaymentMethod.isEmpty()) {
            Toast.makeText(this, "Selecione uma forma de pagamento", Toast.LENGTH_SHORT).show()
            return
        }

        // Obter itens do carrinho
        val cartItems = CartManager.getCartItems()
        if (cartItems.isEmpty()) {
            Toast.makeText(this, "Carrinho vazio!", Toast.LENGTH_SHORT).show()
            return
        }

        // Converter itens do carrinho para itens do pedido
        val orderItems = cartItems.map { cartItem ->
            OrderItem(
                productId = cartItem.product.id,
                productName = cartItem.product.name,
                quantity = cartItem.quantity,
                unitPrice = cartItem.product.price
            )
        }

        // Criar o pedido
        val order = Order(
            id = Order.generateOrderId(),
            userId = UserUtils.getCurrentUserId() ?: "",
            customerName = customerName,
            items = orderItems,
            orderDate = Order.getCurrentDate(),
            paymentMethod = selectedPaymentMethod,
            totalAmount = orderItems.sumOf { it.totalPrice }
        )

        // Mostrar confirmação
        showOrderConfirmation(order, whatsappNumber)
    }

    private fun getSelectedPaymentMethod(): String {
        return when (rgPaymentMethods.checkedRadioButtonId) {
            R.id.rbDebit -> "Cartão de Débito"
            R.id.rbCredit -> "Cartão de Crédito"
            R.id.rbPix -> "PIX"
            R.id.rbCash -> "Dinheiro"
            else -> ""
        }
    }

    private fun showOrderConfirmation(order: Order, customerWhatsApp: String?) {
        val builder = AlertDialog.Builder(this)
        builder.setTitle("Confirmar Pedido")

        val whatsappInfo = if (!customerWhatsApp.isNullOrBlank()) {
            "\nWhatsApp: $customerWhatsApp"
        } else ""

        builder.setMessage(
            "Nome: ${order.customerName}$whatsappInfo\n" +
            "Itens: ${order.items.size} produto(s)\n" +
            "Total: R$ %.2f\n".format(order.items.sumOf { it.totalPrice }) +
            "Pagamento: ${order.paymentMethod} (na retirada)\n" +
            "Local: ${order.pickupLocation}\n\n" +
            "Deseja visualizar o pedido antes de enviar?"
        )

        builder.setPositiveButton("Visualizar") { _, _ ->
            goToOrderPreview(order, customerWhatsApp)
        }

        builder.setNegativeButton("Cancelar") { dialog, _ ->
            dialog.dismiss()
        }

        builder.show()
    }

    private fun goToOrderPreview(order: Order, customerWhatsApp: String?) {
        val intent = Intent(this, OrderPreviewActivity::class.java)
        intent.putExtra(OrderPreviewActivity.EXTRA_ORDER_ID, order.id)
        intent.putExtra(OrderPreviewActivity.EXTRA_CUSTOMER_NAME, order.customerName)
        intent.putExtra(OrderPreviewActivity.EXTRA_PAYMENT_METHOD, order.paymentMethod)

        if (!customerWhatsApp.isNullOrBlank()) {
            intent.putExtra(OrderPreviewActivity.EXTRA_CUSTOMER_WHATSAPP, customerWhatsApp)
        }

        startActivity(intent)
        finish()
    }
}

package com.unasp.unaspmarketplace

import android.app.Activity
import android.content.Intent
import android.net.Uri
import android.os.Bundle
import android.provider.MediaStore
import androidx.appcompat.app.AppCompatActivity
import android.widget.ImageView
import android.widget.Toast
import android.widget.ArrayAdapter
import android.widget.AutoCompleteTextView
import androidx.activity.result.contract.ActivityResultContracts
import androidx.appcompat.app.AlertDialog
import androidx.lifecycle.lifecycleScope
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.RecyclerView
import com.google.android.material.bottomnavigation.BottomNavigationView
import com.google.android.material.button.MaterialButton
import com.google.android.material.textfield.TextInputEditText
import com.google.firebase.storage.FirebaseStorage
import com.unasp.unaspmarketplace.adapters.ProductImageAdapter
import com.unasp.unaspmarketplace.models.Product
import com.unasp.unaspmarketplace.repository.ProductRepository
import kotlinx.coroutines.launch
import kotlinx.coroutines.tasks.await
import java.util.*

class PostItemActivity : AppCompatActivity() {
    private lateinit var productRepository: ProductRepository
    private lateinit var edtName: TextInputEditText
    private lateinit var edtDescription: TextInputEditText
    private lateinit var edtPrice: TextInputEditText
    private lateinit var edtStock: TextInputEditText
    private lateinit var spinnerCategory: AutoCompleteTextView
    private lateinit var btnSave: MaterialButton
    private lateinit var btnCancel: MaterialButton
    private lateinit var btnAddImage: MaterialButton
    private lateinit var btnRemoveImage: MaterialButton
    private lateinit var recyclerImages: RecyclerView

    private lateinit var imageAdapter: ProductImageAdapter
    private val storage = FirebaseStorage.getInstance()
    private val selectedImages = mutableListOf<Uri>()

    private val imagePickerLauncher = registerForActivityResult(
        ActivityResultContracts.StartActivityForResult()
    ) { result ->
        if (result.resultCode == Activity.RESULT_OK) {
            result.data?.data?.let { uri ->
                imageAdapter.addImage(uri)
                btnRemoveImage.isEnabled = imageAdapter.getImages().isNotEmpty()
            }
        }
    }

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.post_item_activity)

        // Inicializar repositório
        productRepository = ProductRepository()

        // Inicializar views
        initViews()
        setupImageRecycler()
        setupCategorySpinner()
        setupButtons()
        setupBottomNavigation()
    }

    private fun initViews() {
        edtName = findViewById(R.id.edtName)
        edtDescription = findViewById(R.id.edtDescription)
        edtPrice = findViewById(R.id.edtPrice)
        edtStock = findViewById(R.id.edtStock)
        spinnerCategory = findViewById(R.id.spinnerCategory)
        btnSave = findViewById(R.id.btnSave)
        btnCancel = findViewById(R.id.btnCancel)
        btnAddImage = findViewById(R.id.btnAddImage)
        btnRemoveImage = findViewById(R.id.btnRemoveImage)
        recyclerImages = findViewById(R.id.recyclerImages)

        val btnBack = findViewById<ImageView>(R.id.btnBack)
        btnBack.setOnClickListener { finish() }

        // Inicialmente desabilitar botão de remover
        btnRemoveImage.isEnabled = false
    }

    private fun setupImageRecycler() {
        imageAdapter = ProductImageAdapter(selectedImages) { position ->
            imageAdapter.removeImage(position)
            btnRemoveImage.isEnabled = imageAdapter.getImages().isNotEmpty()
        }

        recyclerImages.apply {
            adapter = imageAdapter
            layoutManager = LinearLayoutManager(this@PostItemActivity, LinearLayoutManager.HORIZONTAL, false)
        }
    }

    private fun setupCategorySpinner() {
        val categories = arrayOf(
            "Roupas",
            "Eletrônicos",
            "Alimentos",
            "Livros",
            "Casa e Jardim",
            "Esportes",
            "Beleza",
            "Automóveis",
            "Outros"
        )

        val adapter = ArrayAdapter(this, android.R.layout.simple_dropdown_item_1line, categories)
        spinnerCategory.setAdapter(adapter)
    }

    private fun setupButtons() {
        btnSave.setOnClickListener {
            saveProduct()
        }

        btnCancel.setOnClickListener {
            finish()
        }

        btnAddImage.setOnClickListener {
            showImagePickerDialog()
        }

        btnRemoveImage.setOnClickListener {
            if (imageAdapter.getImages().isNotEmpty()) {
                showRemoveImageDialog()
            }
        }
    }

    private fun showImagePickerDialog() {
        val options = arrayOf("Galeria", "Câmera")
        AlertDialog.Builder(this)
            .setTitle("Selecionar Imagem")
            .setItems(options) { _, which ->
                when (which) {
                    0 -> openGallery()
                    1 -> openCamera()
                }
            }
            .show()
    }

    private fun openGallery() {
        val intent = Intent(Intent.ACTION_PICK, MediaStore.Images.Media.EXTERNAL_CONTENT_URI)
        imagePickerLauncher.launch(intent)
    }

    private fun openCamera() {
        val intent = Intent(MediaStore.ACTION_IMAGE_CAPTURE)
        if (intent.resolveActivity(packageManager) != null) {
            imagePickerLauncher.launch(intent)
        } else {
            Toast.makeText(this, "Câmera não disponível", Toast.LENGTH_SHORT).show()
        }
    }

    private fun showRemoveImageDialog() {
        val images = imageAdapter.getImages()
        if (images.isEmpty()) return

        val imageNames = images.mapIndexed { index, _ -> "Imagem ${index + 1}" }.toTypedArray()

        AlertDialog.Builder(this)
            .setTitle("Remover Imagem")
            .setItems(imageNames) { _, which ->
                imageAdapter.removeImage(which)
                btnRemoveImage.isEnabled = imageAdapter.getImages().isNotEmpty()
            }
            .setNegativeButton("Cancelar", null)
            .show()
    }

    private fun saveProduct() {
        // Validar campos
        val name = edtName.text.toString().trim()
        val description = edtDescription.text.toString().trim()
        val priceText = edtPrice.text.toString().trim()
        val stockText = edtStock.text.toString().trim()
        val category = spinnerCategory.text.toString().trim()

        if (name.isEmpty()) {
            edtName.error = "Nome é obrigatório"
            edtName.requestFocus()
            return
        }

        if (description.isEmpty()) {
            edtDescription.error = "Descrição é obrigatória"
            edtDescription.requestFocus()
            return
        }

        if (priceText.isEmpty()) {
            edtPrice.error = "Preço é obrigatório"
            edtPrice.requestFocus()
            return
        }

        if (stockText.isEmpty()) {
            edtStock.error = "Estoque é obrigatório"
            edtStock.requestFocus()
            return
        }

        if (category.isEmpty()) {
            spinnerCategory.error = "Categoria é obrigatória"
            spinnerCategory.requestFocus()
            return
        }

        val price = priceText.toDoubleOrNull()
        if (price == null || price <= 0) {
            edtPrice.error = "Preço deve ser um valor válido"
            edtPrice.requestFocus()
            return
        }

        val stock = stockText.toIntOrNull()
        if (stock == null || stock < 0) {
            edtStock.error = "Estoque deve ser um número válido"
            edtStock.requestFocus()
            return
        }

        // Salvar no Firebase
        btnSave.isEnabled = false
        btnSave.text = "Salvando..."

        lifecycleScope.launch {
            try {
                // Upload das imagens primeiro
                val imageUrls = uploadImages()

                // Criar produto
                val product = Product(
                    name = name,
                    description = description,
                    price = price,
                    stock = stock,
                    category = category,
                    imageUrls = imageUrls,
                    active = true
                )

                val result = productRepository.saveProduct(product)

                runOnUiThread {
                    btnSave.isEnabled = true
                    btnSave.text = " Salvar Produto"

                    if (result.isSuccess) {
                        Toast.makeText(this@PostItemActivity, "Produto salvo com sucesso!", Toast.LENGTH_SHORT).show()

                        // Voltar para a tela anterior
                        val intent = Intent(this@PostItemActivity, HomeActivity::class.java)
                        intent.flags = Intent.FLAG_ACTIVITY_CLEAR_TOP
                        startActivity(intent)
                        finish()
                    } else {
                        val error = result.exceptionOrNull()
                        Toast.makeText(this@PostItemActivity, "Erro ao salvar: ${error?.message}", Toast.LENGTH_LONG).show()
                    }
                }
            } catch (e: Exception) {
                runOnUiThread {
                    btnSave.isEnabled = true
                    btnSave.text = " Salvar Produto"
                    Toast.makeText(this@PostItemActivity, "Erro: ${e.message}", Toast.LENGTH_LONG).show()
                }
            }
        }
    }

    private suspend fun uploadImages(): List<String> {
        val imageUrls = mutableListOf<String>()
        val images = imageAdapter.getImages()

        for ((index, imageUri) in images.withIndex()) {
            try {
                val fileName = "products/${UUID.randomUUID()}_${System.currentTimeMillis()}.jpg"
                val storageRef = storage.reference.child(fileName)

                val uploadTask = storageRef.putFile(imageUri)
                uploadTask.await()

                val downloadUrl = storageRef.downloadUrl.await()
                imageUrls.add(downloadUrl.toString())

                // Atualizar progresso na UI
                runOnUiThread {
                    btnSave.text = "Uploading... ${index + 1}/${images.size}"
                }
            } catch (e: Exception) {
                // Log do erro mas continua com as outras imagens
                e.printStackTrace()
            }
        }

        return imageUrls
    }

    private fun setupBottomNavigation() {
        val bottomNavigation = findViewById<BottomNavigationView>(R.id.bottom_navigation_post)
        bottomNavigation.setOnItemSelectedListener { item ->
            when (item.itemId) {
                R.id.nav_home -> {
                    val intent = Intent(this, HomeActivity::class.java)
                    startActivity(intent)
                    true
                }
                R.id.nav_cart -> {
                    val intent = Intent(this, CartActivity::class.java)
                    startActivity(intent)
                    true
                }
                else -> false
            }
        }
    }
}

package com.unasp.unaspmarketplace

import android.app.Activity
import android.content.Intent
import android.content.pm.PackageManager
import android.net.Uri
import android.os.Bundle
import android.provider.MediaStore
import androidx.appcompat.app.AppCompatActivity
import android.widget.Toast
import android.widget.ArrayAdapter
import android.widget.AutoCompleteTextView
import androidx.activity.result.contract.ActivityResultContracts
import androidx.appcompat.app.AlertDialog
import androidx.core.app.ActivityCompat
import androidx.core.content.ContextCompat
import androidx.lifecycle.lifecycleScope
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.RecyclerView
import com.google.android.material.bottomnavigation.BottomNavigationView
import com.google.android.material.button.MaterialButton
import com.google.android.material.textfield.TextInputEditText
import com.google.firebase.storage.FirebaseStorage
import com.unasp.unaspmarketplace.adapters.ProductImageAdapter
import com.unasp.unaspmarketplace.models.Product
import com.unasp.unaspmarketplace.repository.ProductRepository
import kotlinx.coroutines.launch
import kotlinx.coroutines.tasks.await
import java.util.*

class PostItemActivity : AppCompatActivity() {
    private lateinit var productRepository: ProductRepository
    private lateinit var edtName: TextInputEditText
    private lateinit var edtDescription: TextInputEditText
    private lateinit var edtPrice: TextInputEditText
    private lateinit var edtStock: TextInputEditText
    private lateinit var spinnerCategory: AutoCompleteTextView
    private lateinit var btnSave: MaterialButton
    private lateinit var btnCancel: MaterialButton
    private lateinit var btnAddImage: MaterialButton
    private lateinit var btnRemoveImage: MaterialButton
    private lateinit var recyclerImages: RecyclerView

    private lateinit var imageAdapter: ProductImageAdapter
    private val storage = FirebaseStorage.getInstance()

    companion object {
        private const val CAMERA_PERMISSION_CODE = 100
        private const val STORAGE_PERMISSION_CODE = 101
    }

    private val imagePickerLauncher = registerForActivityResult(
        ActivityResultContracts.StartActivityForResult()
    ) { result ->
        try {
            if (result.resultCode == RESULT_OK) {
                result.data?.data?.let { uri ->
                    // Verificar se o adapter está inicializado
                    if (::imageAdapter.isInitialized) {
                        imageAdapter.addImage(uri)
                        btnRemoveImage.isEnabled = imageAdapter.getImages().isNotEmpty()
                    } else {
                        Toast.makeText(this, "Erro: adapter não inicializado", Toast.LENGTH_SHORT).show()
                    }
                } ?: run {
                    Toast.makeText(this, "Nenhuma imagem selecionada", Toast.LENGTH_SHORT).show()
                }
            }
        } catch (e: Exception) {
            e.printStackTrace()
            Toast.makeText(this, "Erro ao processar imagem: ${e.message}", Toast.LENGTH_SHORT).show()
        }
    }

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.post_item_activity)

        // Inicializar repositório
        productRepository = ProductRepository()

        // Inicializar views
        initViews()
        setupImageRecycler()
        setupCategorySpinner()
        setupButtons()
        setupBottomNavigation()
    }

    private fun initViews() {
        try {
            edtName = findViewById(R.id.edtName)
            edtDescription = findViewById(R.id.edtDescription)
            edtPrice = findViewById(R.id.edtPrice)
            edtStock = findViewById(R.id.edtStock)
            spinnerCategory = findViewById(R.id.spinnerCategory)
            btnSave = findViewById(R.id.btnSave)
            btnCancel = findViewById(R.id.btnCancel)
            btnAddImage = findViewById(R.id.btnAddImage)
            btnRemoveImage = findViewById(R.id.btnRemoveImage)
            recyclerImages = findViewById(R.id.recyclerImages)

            // Configurar toolbar
            val toolbar = findViewById<com.google.android.material.appbar.MaterialToolbar>(R.id.appbar_post)
            toolbar.setNavigationOnClickListener { finish() }

            // Inicialmente desabilitar botão de remover
            btnRemoveImage.isEnabled = false
        } catch (e: Exception) {
            e.printStackTrace()
            Toast.makeText(this, "Erro ao inicializar interface: ${e.message}", Toast.LENGTH_LONG).show()
            finish() // Se não conseguir inicializar as views, fechar a activity
        }
    }

    private fun setupImageRecycler() {
        try {
            imageAdapter = ProductImageAdapter(mutableListOf()) { position ->
                imageAdapter.removeImage(position)
                btnRemoveImage.isEnabled = imageAdapter.getImages().isNotEmpty()
            }

            recyclerImages.apply {
                adapter = imageAdapter
                layoutManager = LinearLayoutManager(this@PostItemActivity, LinearLayoutManager.HORIZONTAL, false)
                setHasFixedSize(true)
                // Adicionar decoração para espaçamento se necessário
            }
        } catch (e: Exception) {
            e.printStackTrace()
            Toast.makeText(this, "Erro ao configurar galeria de imagens: ${e.message}", Toast.LENGTH_SHORT).show()
        }
    }

    private fun setupCategorySpinner() {
        val categories = arrayOf(
            "Roupas",
            "Eletrônicos",
            "Alimentos",
            "Livros",
            "Casa e Jardim",
            "Esportes",
            "Beleza",
            "Automóveis",
            "Outros"
        )

        val adapter = ArrayAdapter(this, android.R.layout.simple_dropdown_item_1line, categories)
        spinnerCategory.setAdapter(adapter)
    }

    private fun setupButtons() {
        btnSave.setOnClickListener {
            saveProduct()
        }

        btnCancel.setOnClickListener {
            finish()
        }

        btnAddImage.setOnClickListener {
            showImagePickerDialog()
        }

        btnRemoveImage.setOnClickListener {
            if (imageAdapter.getImages().isNotEmpty()) {
                showRemoveImageDialog()
            }
        }
    }

    private fun showImagePickerDialog() {
        val options = arrayOf("Galeria", "Câmera")
        AlertDialog.Builder(this)
            .setTitle("Selecionar Imagem")
            .setItems(options) { _, which ->
                when (which) {
                    0 -> openGallery()
                    1 -> openCamera()
                }
            }
            .show()
    }

    private fun openGallery() {
        try {
            if (checkStoragePermission()) {
                val intent = Intent(Intent.ACTION_PICK, MediaStore.Images.Media.EXTERNAL_CONTENT_URI)
                imagePickerLauncher.launch(intent)
            } else {
                requestStoragePermission()
            }
        } catch (e: Exception) {
            Toast.makeText(this, "Erro ao abrir galeria: ${e.message}", Toast.LENGTH_SHORT).show()
        }
    }

    private fun checkStoragePermission(): Boolean {
        return ContextCompat.checkSelfPermission(
            this, 
            android.Manifest.permission.READ_EXTERNAL_STORAGE
        ) == PackageManager.PERMISSION_GRANTED
    }

    private fun requestStoragePermission() {
        ActivityCompat.requestPermissions(
            this,
            arrayOf(android.Manifest.permission.READ_EXTERNAL_STORAGE),
            STORAGE_PERMISSION_CODE
        )
    }

    private fun openCamera() {
        try {
            if (checkCameraPermission()) {
                val intent = Intent(MediaStore.ACTION_IMAGE_CAPTURE)
                if (intent.resolveActivity(packageManager) != null) {
                    imagePickerLauncher.launch(intent)
                } else {
                    Toast.makeText(this, "Câmera não disponível", Toast.LENGTH_SHORT).show()
                }
            } else {
                requestCameraPermission()
            }
        } catch (e: Exception) {
            Toast.makeText(this, "Erro ao abrir câmera: ${e.message}", Toast.LENGTH_SHORT).show()
        }
    }

    private fun checkCameraPermission(): Boolean {
        return ContextCompat.checkSelfPermission(
            this, 
            android.Manifest.permission.CAMERA
        ) == PackageManager.PERMISSION_GRANTED
    }

    private fun requestCameraPermission() {
        ActivityCompat.requestPermissions(
            this,
            arrayOf(android.Manifest.permission.CAMERA),
            CAMERA_PERMISSION_CODE
        )
    }

    private fun showRemoveImageDialog() {
        val images = imageAdapter.getImages()
        if (images.isEmpty()) return

        val imageNames = images.mapIndexed { index, _ -> "Imagem ${index + 1}" }.toTypedArray()

        AlertDialog.Builder(this)
            .setTitle("Remover Imagem")
            .setItems(imageNames) { _, which ->
                imageAdapter.removeImage(which)
                btnRemoveImage.isEnabled = imageAdapter.getImages().isNotEmpty()
            }
            .setNegativeButton("Cancelar", null)
            .show()
    }

    private fun saveProduct() {
        // Validar campos
        val name = edtName.text.toString().trim()
        val description = edtDescription.text.toString().trim()
        val priceText = edtPrice.text.toString().trim()
        val stockText = edtStock.text.toString().trim()
        val category = spinnerCategory.text.toString().trim()

        if (name.isEmpty()) {
            edtName.error = "Nome é obrigatório"
            edtName.requestFocus()
            return
        }

        if (description.isEmpty()) {
            edtDescription.error = "Descrição é obrigatória"
            edtDescription.requestFocus()
            return
        }

        if (priceText.isEmpty()) {
            edtPrice.error = "Preço é obrigatório"
            edtPrice.requestFocus()
            return
        }

        if (stockText.isEmpty()) {
            edtStock.error = "Estoque é obrigatório"
            edtStock.requestFocus()
            return
        }

        if (category.isEmpty()) {
            spinnerCategory.error = "Categoria é obrigatória"
            spinnerCategory.requestFocus()
            return
        }

        val price = priceText.toDoubleOrNull()
        if (price == null || price <= 0) {
            edtPrice.error = "Preço deve ser um valor válido"
            edtPrice.requestFocus()
            return
        }

        val stock = stockText.toIntOrNull()
        if (stock == null || stock < 0) {
            edtStock.error = "Estoque deve ser um número válido"
            edtStock.requestFocus()
            return
        }

        // Salvar no Firebase
        btnSave.isEnabled = false
        btnSave.text = "Salvando..."

        lifecycleScope.launch {
            try {
                // Upload das imagens primeiro
                val imageUrls = uploadImages()

                // Criar produto
                val product = Product(
                    name = name,
                    description = description,
                    price = price,
                    stock = stock,
                    category = category,
                    imageUrls = imageUrls,
                    active = true
                )

                val result = productRepository.saveProduct(product)

                runOnUiThread {
                    btnSave.isEnabled = true
                    btnSave.text = " Salvar Produto"

                    if (result.isSuccess) {
                        Toast.makeText(this@PostItemActivity, "Produto salvo com sucesso!", Toast.LENGTH_SHORT).show()

                        // Voltar para a tela anterior
                        val intent = Intent(this@PostItemActivity, HomeActivity::class.java)
                        intent.flags = Intent.FLAG_ACTIVITY_CLEAR_TOP
                        startActivity(intent)
                        finish()
                    } else {
                        val error = result.exceptionOrNull()
                        Toast.makeText(this@PostItemActivity, "Erro ao salvar: ${error?.message}", Toast.LENGTH_LONG).show()
                    }
                }
            } catch (e: Exception) {
                runOnUiThread {
                    btnSave.isEnabled = true
                    btnSave.text = " Salvar Produto"
                    Toast.makeText(this@PostItemActivity, "Erro: ${e.message}", Toast.LENGTH_LONG).show()
                }
            }
        }
    }

    private suspend fun uploadImages(): List<String> {
        val imageUrls = mutableListOf<String>()
        val images = imageAdapter.getImages()

        for ((index, imageUri) in images.withIndex()) {
            try {
                val fileName = "products/${UUID.randomUUID()}_${System.currentTimeMillis()}.jpg"
                val storageRef = storage.reference.child(fileName)

                val uploadTask = storageRef.putFile(imageUri)
                uploadTask.await()

                val downloadUrl = storageRef.downloadUrl.await()
                imageUrls.add(downloadUrl.toString())

                // Atualizar progresso na UI
                runOnUiThread {
                    btnSave.text = "Uploading... ${index + 1}/${images.size}"
                }
            } catch (e: Exception) {
                // Log do erro mas continua com as outras imagens
                e.printStackTrace()
            }
        }

        return imageUrls
    }

    override fun onRequestPermissionsResult(
        requestCode: Int,
        permissions: Array<out String>,
        grantResults: IntArray
    ) {
        super.onRequestPermissionsResult(requestCode, permissions, grantResults)
        when (requestCode) {
            CAMERA_PERMISSION_CODE -> {
                if (grantResults.isNotEmpty() && grantResults[0] == PackageManager.PERMISSION_GRANTED) {
                    openCamera()
                } else {
                    Toast.makeText(this, "Permissão de câmera negada", Toast.LENGTH_SHORT).show()
                }
            }
            STORAGE_PERMISSION_CODE -> {
                if (grantResults.isNotEmpty() && grantResults[0] == PackageManager.PERMISSION_GRANTED) {
                    openGallery()
                } else {
                    Toast.makeText(this, "Permissão de armazenamento negada", Toast.LENGTH_SHORT).show()
                }
            }
        }
    }

    private fun setupBottomNavigation() {
        val bottomNavigation = findViewById<BottomNavigationView>(R.id.bottom_navigation_post)
        bottomNavigation.setOnItemSelectedListener { item ->
            when (item.itemId) {
                R.id.nav_home -> {
                    val intent = Intent(this, HomeActivity::class.java)
                    startActivity(intent)
                    true
                }
                R.id.nav_cart -> {
                    val intent = Intent(this, CartActivity::class.java)
                    startActivity(intent)
                    true
                }
                else -> false
            }
        }
    }
}

package com.unasp.unaspmarketplace

import android.app.AlertDialog
import android.content.Intent
import android.os.Bundle
import android.widget.Toast
import androidx.appcompat.app.AppCompatActivity
import androidx.lifecycle.lifecycleScope
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.RecyclerView
import androidx.swiperefreshlayout.widget.SwipeRefreshLayout
import com.google.firebase.auth.FirebaseAuth
import com.google.firebase.firestore.FirebaseFirestore
import com.google.firebase.firestore.Query
import com.unasp.unaspmarketplace.adapters.PostedItemsAdapter
import com.unasp.unaspmarketplace.models.Product
import kotlinx.coroutines.launch
import kotlinx.coroutines.tasks.await

class PostedItemsActivity : AppCompatActivity() {

    private lateinit var recyclerView: RecyclerView
    private lateinit var swipeRefresh: SwipeRefreshLayout
    private lateinit var postedItemsAdapter: PostedItemsAdapter
    private val productsList = mutableListOf<Product>()

    private val firestore = FirebaseFirestore.getInstance()
    private val auth = FirebaseAuth.getInstance()

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_posted_items)

        setupToolbar()
        setupSwipeRefresh()
        setupRecyclerView()
        loadPostedItems()
    }

    private fun setupToolbar() {
        val toolbar = findViewById<androidx.appcompat.widget.Toolbar>(R.id.toolbar)
        setSupportActionBar(toolbar)
        supportActionBar?.setDisplayHomeAsUpEnabled(true)
        supportActionBar?.title = "Meus Itens Postados"
        toolbar.setNavigationOnClickListener { finish() }
    }

    private fun setupSwipeRefresh() {
        swipeRefresh = findViewById(R.id.swipeRefresh)
        swipeRefresh.setOnRefreshListener {
            loadPostedItems()
        }
    }

    private fun setupRecyclerView() {
        recyclerView = findViewById(R.id.recyclerPostedItems)
        postedItemsAdapter = PostedItemsAdapter(
            productsList,
            onEditClick = { product -> editProduct(product) },
            onToggleVisibilityClick = { product -> toggleProductVisibility(product) },
            onDeleteClick = { product -> confirmDeleteProduct(product) }
        )

        recyclerView.apply {
            layoutManager = LinearLayoutManager(this@PostedItemsActivity)
            adapter = postedItemsAdapter
        }
    }

    private fun loadPostedItems() {
        val userId = auth.currentUser?.uid ?: return

        // Mostrar loading apenas se não for refresh
        if (!swipeRefresh.isRefreshing) {
            swipeRefresh.isRefreshing = true
        }

        lifecycleScope.launch {
            try {
                val productsSnapshot = firestore.collection("products")
                    .whereEqualTo("sellerId", userId)
                    .orderBy("createdAt", Query.Direction.DESCENDING)
                    .get()
                    .await()

                productsList.clear()
                for (document in productsSnapshot.documents) {
                    val product = document.toObject(Product::class.java)?.copy(id = document.id)
                    product?.let { productsList.add(it) }
                }

                postedItemsAdapter.notifyDataSetChanged()

                if (productsList.isEmpty()) {
                    Toast.makeText(this@PostedItemsActivity, "Você ainda não postou nenhum item", Toast.LENGTH_SHORT).show()
                }

            } catch (e: Exception) {
                Toast.makeText(this@PostedItemsActivity, "Erro ao carregar itens: ${e.message}", Toast.LENGTH_LONG).show()
            } finally {
                swipeRefresh.isRefreshing = false
            }
        }
    }

    private fun editProduct(product: Product) {
        // Abrir tela de edição de produto
        val intent = Intent(this, PostItemActivity::class.java).apply {
            putExtra("productId", product.id)
            putExtra("editMode", true)
        }
        startActivity(intent)
    }

    private fun toggleProductVisibility(product: Product) {
        lifecycleScope.launch {
            try {
                val newActiveStatus = !product.active

                firestore.collection("products")
                    .document(product.id)
                    .update("active", newActiveStatus)
                    .await()

                // Atualizar na lista local
                val index = productsList.indexOfFirst { it.id == product.id }
                if (index != -1) {
                    productsList[index] = product.copy(active = newActiveStatus)
                    postedItemsAdapter.notifyItemChanged(index)
                }

                val status = if (newActiveStatus) "visível" else "oculto"
                Toast.makeText(this@PostedItemsActivity, "Produto agora está $status", Toast.LENGTH_SHORT).show()

            } catch (e: Exception) {
                Toast.makeText(this@PostedItemsActivity, "Erro ao alterar visibilidade: ${e.message}", Toast.LENGTH_LONG).show()
            }
        }
    }

    private fun confirmDeleteProduct(product: Product) {
        AlertDialog.Builder(this)
            .setTitle("Remover Produto")
            .setMessage("Tem certeza que deseja remover \"${product.name}\"? Esta ação não pode ser desfeita.")
            .setPositiveButton("Remover") { _, _ ->
                deleteProduct(product)
            }
            .setNegativeButton("Cancelar", null)
            .show()
    }

    private fun deleteProduct(product: Product) {
        lifecycleScope.launch {
            try {
                firestore.collection("products")
                    .document(product.id)
                    .delete()
                    .await()

                // Remover da lista local
                val index = productsList.indexOfFirst { it.id == product.id }
                if (index != -1) {
                    productsList.removeAt(index)
                    postedItemsAdapter.notifyItemRemoved(index)
                }

                Toast.makeText(this@PostedItemsActivity, "Produto removido com sucesso", Toast.LENGTH_SHORT).show()

            } catch (e: Exception) {
                Toast.makeText(this@PostedItemsActivity, "Erro ao remover produto: ${e.message}", Toast.LENGTH_LONG).show()
            }
        }
    }

    override fun onResume() {
        super.onResume()
        // Recarregar lista quando voltar para a tela (caso tenha editado algum produto)
        loadPostedItems()
    }
}

package com.unasp.unaspmarketplace.adapters

import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import android.widget.TextView
import androidx.recyclerview.widget.RecyclerView
import com.unasp.unaspmarketplace.R
import com.unasp.unaspmarketplace.models.Order
import java.text.SimpleDateFormat
import java.util.*

class OrderHistoryAdapter(
    private val orders: List<Order>,
    private val onOrderClick: (Order) -> Unit
) : RecyclerView.Adapter<OrderHistoryAdapter.OrderViewHolder>() {

    class OrderViewHolder(view: View) : RecyclerView.ViewHolder(view) {
        val tvOrderId: TextView = view.findViewById(R.id.tvOrderId)
        val tvOrderDate: TextView = view.findViewById(R.id.tvOrderDate)
        val tvOrderItems: TextView = view.findViewById(R.id.tvOrderItems)
        val tvOrderTotal: TextView = view.findViewById(R.id.tvOrderTotal)
        val tvOrderStatus: TextView = view.findViewById(R.id.tvOrderStatus)
    }

    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): OrderViewHolder {
        val view = LayoutInflater.from(parent.context)
            .inflate(R.layout.item_order_history, parent, false)
        return OrderViewHolder(view)
    }

    override fun onBindViewHolder(holder: OrderViewHolder, position: Int) {
        val order = orders[position]

        holder.tvOrderId.text = "Pedido #${order.id}"
        holder.tvOrderDate.text = order.orderDate

        // Mostrar quantidade de itens
        val itemCount = order.items.size
        val itemText = if (itemCount == 1) "1 item" else "$itemCount itens"
        holder.tvOrderItems.text = itemText

        // Calcular total
        val total = order.items.sumOf { it.totalPrice }
        holder.tvOrderTotal.text = "R$ ${String.format("%.2f", total)}"

        // Status do pedido (pode ser expandido futuramente)
        holder.tvOrderStatus.text = "Concluído"

        holder.itemView.setOnClickListener {
            onOrderClick(order)
        }
    }

    override fun getItemCount() = orders.size
}

package com.unasp.unaspmarketplace.adapters

import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import android.widget.ImageButton
import android.widget.ImageView
import android.widget.TextView
import androidx.recyclerview.widget.RecyclerView
import com.bumptech.glide.Glide
import com.unasp.unaspmarketplace.R
import com.unasp.unaspmarketplace.models.Product

class PostedItemsAdapter(
    private val products: List<Product>,
    private val onEditClick: (Product) -> Unit,
    private val onToggleVisibilityClick: (Product) -> Unit,
    private val onDeleteClick: (Product) -> Unit
) : RecyclerView.Adapter<PostedItemsAdapter.ProductViewHolder>() {

    class ProductViewHolder(view: View) : RecyclerView.ViewHolder(view) {
        val imgProduct: ImageView = view.findViewById(R.id.imgProduct)
        val tvProductName: TextView = view.findViewById(R.id.tvProductName)
        val tvProductPrice: TextView = view.findViewById(R.id.tvProductPrice)
        val tvProductStock: TextView = view.findViewById(R.id.tvProductStock)
        val tvProductStatus: TextView = view.findViewById(R.id.tvProductStatus)
        val btnEdit: ImageButton = view.findViewById(R.id.btnEdit)
        val btnToggleVisibility: ImageButton = view.findViewById(R.id.btnToggleVisibility)
        val btnDelete: ImageButton = view.findViewById(R.id.btnDelete)
    }

    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ProductViewHolder {
        val view = LayoutInflater.from(parent.context)
            .inflate(R.layout.item_posted_product, parent, false)
        return ProductViewHolder(view)
    }

    override fun onBindViewHolder(holder: ProductViewHolder, position: Int) {
        val product = products[position]

        // Carregar imagem do produto
        if (product.imageUrls.isNotEmpty()) {
            Glide.with(holder.itemView.context)
                .load(product.imageUrls.first())
                .placeholder(R.drawable.ic_launcher_background)
                .error(R.drawable.ic_launcher_background)
                .centerCrop()
                .into(holder.imgProduct)
        } else {
            holder.imgProduct.setImageResource(R.drawable.ic_launcher_background)
        }

        holder.tvProductName.text = product.name
        holder.tvProductPrice.text = "R$ ${String.format("%.2f", product.price)}"
        holder.tvProductStock.text = "Estoque: ${product.stock}"

        // Status do produto
        if (product.active) {
            holder.tvProductStatus.text = "Visível"
            holder.tvProductStatus.setTextColor(holder.itemView.context.getColor(android.R.color.holo_green_dark))
            holder.btnToggleVisibility.setImageResource(R.drawable.ic_visibility)
        } else {
            holder.tvProductStatus.text = "Oculto"
            holder.tvProductStatus.setTextColor(holder.itemView.context.getColor(android.R.color.holo_red_dark))
            holder.btnToggleVisibility.setImageResource(R.drawable.ic_visibility_off)
        }

        // Configurar cliques dos botões
        holder.btnEdit.setOnClickListener {
            onEditClick(product)
        }

        holder.btnToggleVisibility.setOnClickListener {
            onToggleVisibilityClick(product)
        }

        holder.btnDelete.setOnClickListener {
            onDeleteClick(product)
        }
    }

    override fun getItemCount() = products.size
}

package com.unasp.unaspmarketplace.adapters

import android.net.Uri
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import android.widget.ImageButton
import android.widget.ImageView
import androidx.recyclerview.widget.RecyclerView
import com.bumptech.glide.Glide
import com.unasp.unaspmarketplace.R

class ProductImageAdapter(
    private val images: MutableList<Uri>,
    private val onRemoveClick: (Int) -> Unit
) : RecyclerView.Adapter<ProductImageAdapter.ImageViewHolder>() {

    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ImageViewHolder {
        val view = LayoutInflater.from(parent.context)
            .inflate(R.layout.item_product_image, parent, false)
        return ImageViewHolder(view)
    }

    override fun onBindViewHolder(holder: ImageViewHolder, position: Int) {
        holder.bind(images[position], position)
    }

    override fun getItemCount(): Int = images.size

    fun addImage(uri: Uri) {
        images.add(uri)
        notifyItemInserted(images.size - 1)
    }

    fun removeImage(position: Int) {
        if (position in 0 until images.size) {
            images.removeAt(position)
            notifyItemRemoved(position)
            notifyItemRangeChanged(position, images.size)
        }
    }

    fun getImages(): List<Uri> = images.toList()

    inner class ImageViewHolder(itemView: View) : RecyclerView.ViewHolder(itemView) {
        private val imageView: ImageView = itemView.findViewById(R.id.ivProductImage)
        private val btnRemove: ImageButton = itemView.findViewById(R.id.btnRemoveImage)

        fun bind(uri: Uri, position: Int) {
            Glide.with(itemView.context)
                .load(uri)
                .centerCrop()
                .into(imageView)

            btnRemove.setOnClickListener {
                onRemoveClick(position)
            }
        }
    }
}

package com.unasp.unaspmarketplace.adapters

import android.net.Uri
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import android.widget.ImageButton
import android.widget.ImageView
import androidx.recyclerview.widget.RecyclerView
import com.bumptech.glide.Glide
import com.unasp.unaspmarketplace.R

class ProductImageAdapter(
    private val images: MutableList<Uri>,
    private val onRemoveClick: (Int) -> Unit
) : RecyclerView.Adapter<ProductImageAdapter.ImageViewHolder>() {

    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ImageViewHolder {
        val view = LayoutInflater.from(parent.context)
            .inflate(R.layout.item_product_image, parent, false)
        return ImageViewHolder(view)
    }

    override fun onBindViewHolder(holder: ImageViewHolder, position: Int) {
        holder.bind(images[position], position)
    }

    override fun getItemCount(): Int = images.size

    fun addImage(uri: Uri) {
        images.add(uri)
        notifyItemInserted(images.size - 1)
    }

    fun removeImage(position: Int) {
        if (position in 0 until images.size) {
            images.removeAt(position)
            notifyItemRemoved(position)
            notifyItemRangeChanged(position, images.size)
        }
    }

    fun getImages(): List<Uri> = images.toList()

    inner class ImageViewHolder(itemView: View) : RecyclerView.ViewHolder(itemView) {
        private val imageView: ImageView = itemView.findViewById(R.id.ivProductImage)
        private val btnRemove: ImageButton = itemView.findViewById(R.id.btnRemoveImage)

        fun bind(uri: Uri, position: Int) {
            try {
                Glide.with(itemView.context)
                    .load(uri)
                    .centerCrop()
                    .placeholder(R.color.gray_light)
                    .error(R.color.gray_light)
                    .into(imageView)

                btnRemove.setOnClickListener {
                    onRemoveClick(position)
                }
            } catch (e: Exception) {
                e.printStackTrace()
                // Fallback para erro de carregamento de imagem
                imageView.setBackgroundColor(itemView.context.getColor(R.color.gray_light))
            }
        }
    }
}

package com.unasp.unaspmarketplace.models

import java.text.SimpleDateFormat
import java.util.*

data class Order(
    val id: String,
    val customerName: String,
    val pickupLocation: String = "UNASP Store",
    val items: List<OrderItem>,
    val orderDate: String,
    val paymentMethod: String
) {
    companion object {
        fun generateOrderId(): String {
            val timestamp = System.currentTimeMillis()
            return "PED${timestamp.toString().takeLast(8)}"
        }

        fun getCurrentDate(): String {
            val dateFormat = SimpleDateFormat("dd/MM/yyyy HH:mm", Locale.getDefault())
            return dateFormat.format(Date())
        }
    }

    fun formatForWhatsApp(): String {
        val itemsList = items.joinToString("\n") { "• ${it.productName} (Qtd: ${it.quantity}) - R$ ${String.format("%.2f", it.totalPrice)}" }
        val totalAmount = items.sumOf { it.totalPrice }

        return """
 *NOVO PEDIDO - UNASP MARKETPLACE*

 *ID do Pedido:* $id
 *Nome:* $customerName
 *Local de Retirada:* $pickupLocation
 *Data da Compra:* $orderDate
 *Forma de Pagamento:* $paymentMethod (na retirada)

️ *Itens Comprados:*
$itemsList

 *Total:* R$ ${String.format("%.2f", totalAmount)}

_Por favor, confirme o recebimento deste pedido._
        """.trimIndent()
    }
}

data class OrderItem(
    val productName: String,
    val quantity: Int,
    val unitPrice: Double,
    val totalPrice: Double
)

package com.unasp.unaspmarketplace.models

import java.text.SimpleDateFormat
import java.util.*

data class Order(
    val id: String = "",
    val userId: String = "", // ID do usuário que fez o pedido
    val customerName: String = "",
    val pickupLocation: String = "UNASP Store",
    val items: List<OrderItem> = emptyList(),
    val orderDate: String = "",
    val paymentMethod: String = "",
    val status: String = "Concluído", // Pode ser expandido futuramente
    val timestamp: Long = System.currentTimeMillis(),
    val totalAmount: Double = 0.0
) {
    // Construtor vazio necessário para o Firebase
    constructor() : this("", "", "", "UNASP Store", emptyList(), "", "", "Concluído", System.currentTimeMillis(), 0.0)

    companion object {
        fun generateOrderId(): String {
            val timestamp = System.currentTimeMillis()
            return "PED${timestamp.toString().takeLast(8)}"
        }

        fun getCurrentDate(): String {
            val dateFormat = SimpleDateFormat("dd/MM/yyyy HH:mm", Locale.getDefault())
            return dateFormat.format(Date())
        }
    }

    fun formatForWhatsApp(): String {
        val itemsList = items.joinToString("\n") { "• ${it.productName} (Qtd: ${it.quantity}) - R$ ${String.format("%.2f", it.totalPrice)}" }
        val calculatedTotal = items.sumOf { it.totalPrice }

        return """
 *NOVO PEDIDO - UNASP MARKETPLACE*

 *ID do Pedido:* $id
 *Nome:* $customerName
 *Local de Retirada:* $pickupLocation
 *Data da Compra:* $orderDate
 *Forma de Pagamento:* $paymentMethod (na retirada)

️ *Itens Comprados:*
$itemsList

 *Total:* R$ ${String.format("%.2f", calculatedTotal)}

_Por favor, confirme o recebimento deste pedido._
        """.trimIndent()
    }
}

package com.unasp.unaspmarketplace.models

data class OrderItem(
    val productId: String = "",
    val productName: String = "",
    val quantity: Int = 0,
    val unitPrice: Double = 0.0
) {
    val totalPrice: Double
        get() = quantity * unitPrice

    // Construtor vazio necessário para o Firebase
    constructor() : this("", "", 0, 0.0)
}

package com.unasp.unaspmarketplace.utils

import android.util.Log
import com.google.firebase.auth.FirebaseAuth
import com.google.firebase.firestore.FirebaseFirestore
import com.unasp.unaspmarketplace.models.User
import kotlinx.coroutines.tasks.await

object UserUtils {
    private const val TAG = "UserUtils"
    private const val USERS_COLLECTION = "users"

    private val auth = FirebaseAuth.getInstance()
    private val firestore = FirebaseFirestore.getInstance()

    suspend fun ensureUserDataExists() {
        val currentUser = auth.currentUser ?: return

        try {
            val userDoc = firestore.collection(USERS_COLLECTION)
                .document(currentUser.uid)
                .get()
                .await()

            if (!userDoc.exists()) {
                // Criar novo usuário no Firestore
                val user = User(
                    id = currentUser.uid,
                    name = currentUser.displayName ?: "",
                    email = currentUser.email ?: "",
                    profileImageUrl = currentUser.photoUrl?.toString() ?: ""
                )

                firestore.collection(USERS_COLLECTION)
                    .document(currentUser.uid)
                    .set(user)
                    .await()

                Log.d(TAG, "Usuário criado no Firestore: ${user.email}")
            }
        } catch (e: Exception) {
            Log.e(TAG, "Erro ao garantir dados do usuário", e)
            throw e
        }
    }

    suspend fun getCurrentUser(): User? {
        val currentUser = auth.currentUser ?: return null

        return try {
            val userDoc = firestore.collection(USERS_COLLECTION)
                .document(currentUser.uid)
                .get()
                .await()

            userDoc.toObject(User::class.java)
        } catch (e: Exception) {
            Log.e(TAG, "Erro ao buscar dados do usuário", e)
            null
        }
    }

    suspend fun updateUser(user: User): Boolean {
        val currentUser = auth.currentUser ?: return false

        return try {
            firestore.collection(USERS_COLLECTION)
                .document(currentUser.uid)
                .set(user)
                .await()
            true
        } catch (e: Exception) {
            Log.e(TAG, "Erro ao atualizar usuário", e)
            false
        }
    }

    suspend fun updateUserName(newName: String): Boolean {
        val currentUser = auth.currentUser ?: return false

        return try {
            firestore.collection(USERS_COLLECTION)
                .document(currentUser.uid)
                .update("name", newName)
                .await()
            true
        } catch (e: Exception) {
            Log.e(TAG, "Erro ao atualizar nome do usuário", e)
            false
        }
    }

    suspend fun updateUserWhatsApp(whatsappNumber: String): Boolean {
        val currentUser = auth.currentUser ?: return false

        return try {
            firestore.collection(USERS_COLLECTION)
                .document(currentUser.uid)
                .update("whatsappNumber", whatsappNumber)
                .await()
            true
        } catch (e: Exception) {
            Log.e(TAG, "Erro ao atualizar WhatsApp do usuário", e)
            false
        }
    }

    suspend fun updateUserProfileImage(imageUrl: String): Boolean {
        val currentUser = auth.currentUser ?: return false

        return try {
            firestore.collection(USERS_COLLECTION)
                .document(currentUser.uid)
                .update("profileImageUrl", imageUrl)
                .await()
            true
        } catch (e: Exception) {
            Log.e(TAG, "Erro ao atualizar foto do usuário", e)
            false
        }
    }
}

package com.unasp.unaspmarketplace.utils

import android.util.Log
import com.google.firebase.auth.FirebaseAuth
import com.google.firebase.firestore.FirebaseFirestore
import com.unasp.unaspmarketplace.models.User
import kotlinx.coroutines.tasks.await

object UserUtils {
    private const val TAG = "UserUtils"
    private const val USERS_COLLECTION = "users"

    private val auth = FirebaseAuth.getInstance()
    private val firestore = FirebaseFirestore.getInstance()

    suspend fun ensureUserDataExists() {
        val currentUser = auth.currentUser ?: return

        try {
            val userDoc = firestore.collection(USERS_COLLECTION)
                .document(currentUser.uid)
                .get()
                .await()

            if (!userDoc.exists()) {
                // Criar novo usuário no Firestore
                val user = User(
                    id = currentUser.uid,
                    name = currentUser.displayName ?: "",
                    email = currentUser.email ?: "",
                    profileImageUrl = currentUser.photoUrl?.toString() ?: ""
                )

                firestore.collection(USERS_COLLECTION)
                    .document(currentUser.uid)
                    .set(user)
                    .await()

                Log.d(TAG, "Usuário criado no Firestore: ${user.email}")
            }
        } catch (e: Exception) {
            Log.e(TAG, "Erro ao garantir dados do usuário", e)
            throw e
        }
    }

    suspend fun getCurrentUser(): User? {
        val currentUser = auth.currentUser ?: return null

        return try {
            val userDoc = firestore.collection(USERS_COLLECTION)
                .document(currentUser.uid)
                .get()
                .await()

            userDoc.toObject(User::class.java)
        } catch (e: Exception) {
            Log.e(TAG, "Erro ao buscar dados do usuário", e)
            null
        }
    }

    suspend fun updateUser(user: User): Boolean {
        val currentUser = auth.currentUser ?: return false

        return try {
            firestore.collection(USERS_COLLECTION)
                .document(currentUser.uid)
                .set(user)
                .await()
            true
        } catch (e: Exception) {
            Log.e(TAG, "Erro ao atualizar usuário", e)
            false
        }
    }

    suspend fun updateUserName(newName: String): Boolean {
        val currentUser = auth.currentUser ?: return false

        return try {
            firestore.collection(USERS_COLLECTION)
                .document(currentUser.uid)
                .update("name", newName)
                .await()
            true
        } catch (e: Exception) {
            Log.e(TAG, "Erro ao atualizar nome do usuário", e)
            false
        }
    }

    suspend fun updateUserWhatsApp(whatsappNumber: String): Boolean {
        val currentUser = auth.currentUser ?: return false

        return try {
            firestore.collection(USERS_COLLECTION)
                .document(currentUser.uid)
                .update("whatsappNumber", whatsappNumber)
                .await()
            true
        } catch (e: Exception) {
            Log.e(TAG, "Erro ao atualizar WhatsApp do usuário", e)
            false
        }
    }

    suspend fun updateUserProfileImage(imageUrl: String): Boolean {
        val currentUser = auth.currentUser ?: return false

        return try {
            firestore.collection(USERS_COLLECTION)
                .document(currentUser.uid)
                .update("profileImageUrl", imageUrl)
                .await()
            true
        } catch (e: Exception) {
            Log.e(TAG, "Erro ao atualizar foto do usuário", e)
            false
        }
    }

    /**
     * Retorna o ID do usuário atualmente logado
     */
    fun getCurrentUserId(): String? {
        return auth.currentUser?.uid
    }

    /**
     * Verifica se há um usuário logado
     */
    fun isUserLoggedIn(): Boolean {
        return auth.currentUser != null
    }
}

package com.unasp.unaspmarketplace.utils

import android.content.Context
import android.content.Intent
import android.net.Uri
import android.widget.Toast
import android.util.Log

object WhatsAppHelper {

    private const val TAG = "WhatsAppHelper"

    fun sendMessage(context: Context, message: String) {
        val phoneNumber = Constants.UNASP_STORE_PHONE

        try {
            // Método mais simples - tentar abrir diretamente
            val url = "https://wa.me/$phoneNumber?text=${Uri.encode(message)}"
            val intent = Intent(Intent.ACTION_VIEW, Uri.parse(url))

            if (intent.resolveActivity(context.packageManager) != null) {
                context.startActivity(intent)
                Log.d(TAG, "WhatsApp aberto com sucesso")
            } else {
                // Se não conseguir, copiar mensagem
                copyToClipboard(context, message)
                Toast.makeText(context, 
                    "WhatsApp não encontrado. Mensagem copiada!\nEnvie para: $phoneNumber", 
                    Toast.LENGTH_LONG).show()
            }

        } catch (e: Exception) {
            Log.e(TAG, "Erro ao abrir WhatsApp", e)
            copyToClipboard(context, message)
            Toast.makeText(context, 
                "Erro ao abrir WhatsApp. Mensagem copiada!\nEnvie para: $phoneNumber", 
                Toast.LENGTH_LONG).show()
        }
    }

    private fun copyToClipboard(context: Context, text: String) {
        val clipboard = context.getSystemService(Context.CLIPBOARD_SERVICE) as android.content.ClipboardManager
        val clip = android.content.ClipData.newPlainText("Pedido UNASP", text)
        clipboard.setPrimaryClip(clip)
    }
}

package com.unasp.unaspmarketplace.utils

import android.content.Context
import android.content.Intent
import android.net.Uri
import android.widget.Toast

object WhatsAppManager {

    fun sendOrderToWhatsApp(context: Context, orderMessage: String) {
        try {
            val phoneNumber = Constants.UNASP_STORE_PHONE

            // Primeiro tenta abrir o WhatsApp Business
            val intentBusiness = Intent(Intent.ACTION_VIEW).apply {
                data = Uri.parse("https://wa.me/$phoneNumber?text=${Uri.encode(orderMessage)}")
                setPackage("com.whatsapp.w4b")
            }

            if (intentBusiness.resolveActivity(context.packageManager) != null) {
                context.startActivity(intentBusiness)
                return
            }

            // Se não tiver WhatsApp Business, tenta o WhatsApp normal
            val intentNormal = Intent(Intent.ACTION_VIEW).apply {
                data = Uri.parse("https://wa.me/$phoneNumber?text=${Uri.encode(orderMessage)}")
                setPackage("com.whatsapp")
            }

            if (intentNormal.resolveActivity(context.packageManager) != null) {
                context.startActivity(intentNormal)
                return
            }

            // Se não tiver WhatsApp instalado, abre no navegador
            val intentBrowser = Intent(Intent.ACTION_VIEW).apply {
                data = Uri.parse("https://wa.me/$phoneNumber?text=${Uri.encode(orderMessage)}")
            }

            if (intentBrowser.resolveActivity(context.packageManager) != null) {
                context.startActivity(intentBrowser)
                Toast.makeText(context, Constants.WHATSAPP_NOT_FOUND, Toast.LENGTH_LONG).show()
            } else {
                // Fallback: copiar para área de transferência
                copyToClipboard(context, orderMessage)
                Toast.makeText(context, "Mensagem copiada! Cole no WhatsApp da ${Constants.UNASP_STORE_NAME}", Toast.LENGTH_LONG).show()
            }

        } catch (e: Exception) {
            Toast.makeText(context, "Erro ao abrir WhatsApp: ${e.message}", Toast.LENGTH_LONG).show()
            // Fallback: copiar para área de transferência
            copyToClipboard(context, orderMessage)
        }
    }

    private fun copyToClipboard(context: Context, text: String) {
        val clipboard = context.getSystemService(Context.CLIPBOARD_SERVICE) as android.content.ClipboardManager
        val clip = android.content.ClipData.newPlainText("Pedido UNASP Marketplace", text)
        clipboard.setPrimaryClip(clip)
    }

    fun isWhatsAppInstalled(context: Context): Boolean {
        val packageManager = context.packageManager
        return try {
            packageManager.getPackageInfo("com.whatsapp", 0)
            true
        } catch (e: Exception) {
            try {
                packageManager.getPackageInfo("com.whatsapp.w4b", 0)
                true
            } catch (e: Exception) {
                false
            }
        }
    }
}

package com.unasp.unaspmarketplace.utils

import android.content.Context
import android.content.Intent
import android.net.Uri
import android.widget.Toast
import android.util.Log

object WhatsAppManager {

    private const val TAG = "WhatsAppManager"

    fun sendOrderToWhatsApp(context: Context, orderMessage: String) {
        try {
            val phoneNumber = Constants.UNASP_STORE_PHONE
            Log.d(TAG, "Tentando enviar para número: $phoneNumber")

            // Criar URL do WhatsApp
            val whatsappUrl = "https://wa.me/$phoneNumber?text=${Uri.encode(orderMessage)}"
            Log.d(TAG, "URL gerada: $whatsappUrl")

            // Método 1: Tentar abrir WhatsApp sem especificar package (mais genérico)
            val intentGeneric = Intent(Intent.ACTION_VIEW).apply {
                data = Uri.parse(whatsappUrl)
            }

            // Verificar se algum app pode lidar com wa.me
            val packageManager = context.packageManager
            val activities = packageManager.queryIntentActivities(intentGeneric, 0)

            // Procurar por WhatsApp nas atividades disponíveis
            val whatsappActivity = activities.find {
                it.activityInfo.packageName.contains("whatsapp")
            }

            if (whatsappActivity != null) {
                Log.d(TAG, "Encontrou WhatsApp: ${whatsappActivity.activityInfo.packageName}")
                intentGeneric.setPackage(whatsappActivity.activityInfo.packageName)
                context.startActivity(intentGeneric)
                return
            }

            // Método 2: Tentar WhatsApp normal
            val intentNormal = Intent(Intent.ACTION_VIEW).apply {
                data = Uri.parse(whatsappUrl)
                setPackage("com.whatsapp")
            }

            if (intentNormal.resolveActivity(context.packageManager) != null) {
                Log.d(TAG, "Abrindo WhatsApp normal")
                context.startActivity(intentNormal)
                return
            }

            // Método 3: Tentar WhatsApp Business
            val intentBusiness = Intent(Intent.ACTION_VIEW).apply {
                data = Uri.parse(whatsappUrl)
                setPackage("com.whatsapp.w4b")
            }

            if (intentBusiness.resolveActivity(context.packageManager) != null) {
                Log.d(TAG, "Abrindo WhatsApp Business")
                context.startActivity(intentBusiness)
                return
            }

            // Método 4: Tentar direct intent para WhatsApp
            val directIntent = Intent().apply {
                action = Intent.ACTION_SEND
                putExtra(Intent.EXTRA_TEXT, orderMessage)
                putExtra("jid", "$phoneNumber@s.whatsapp.net")
                setPackage("com.whatsapp")
                type = "text/plain"
            }

            if (directIntent.resolveActivity(context.packageManager) != null) {
                Log.d(TAG, "Usando intent direto do WhatsApp")
                context.startActivity(directIntent)
                return
            }

            // Método 5: Se não tiver WhatsApp, abrir no navegador
            val intentBrowser = Intent(Intent.ACTION_VIEW).apply {
                data = Uri.parse(whatsappUrl)
            }

            if (intentBrowser.resolveActivity(context.packageManager) != null) {
                Log.d(TAG, "Abrindo no navegador")
                context.startActivity(intentBrowser)
                Toast.makeText(context, "WhatsApp não encontrado. Abrindo no navegador...", Toast.LENGTH_SHORT).show()
            } else {
                Log.w(TAG, "Nenhuma opção funcionou, copiando para clipboard")
                // Fallback: copiar para área de transferência
                copyToClipboard(context, orderMessage)
                showFallbackMessage(context, phoneNumber, orderMessage)
            }

        } catch (e: Exception) {
            Log.e(TAG, "Erro ao abrir WhatsApp", e)
            Toast.makeText(context, "Erro ao abrir WhatsApp: ${e.message}", Toast.LENGTH_LONG).show()
            // Fallback: copiar para área de transferência
            copyToClipboard(context, orderMessage)
            showFallbackMessage(context, Constants.UNASP_STORE_PHONE, orderMessage)
        }
    }

    private fun showFallbackMessage(context: Context, phoneNumber: String, orderMessage: String) {
        Toast.makeText(context,
            "Pedido copiado! Envie manualmente para: $phoneNumber\nOu abra o WhatsApp e cole a mensagem.",
            Toast.LENGTH_LONG).show()
    }

    private fun copyToClipboard(context: Context, text: String) {
        val clipboard = context.getSystemService(Context.CLIPBOARD_SERVICE) as android.content.ClipboardManager
        val clip = android.content.ClipData.newPlainText("Pedido UNASP Marketplace", text)
        clipboard.setPrimaryClip(clip)
    }

    fun isWhatsAppInstalled(context: Context): Boolean {
        val packageManager = context.packageManager
        return try {
            packageManager.getPackageInfo("com.whatsapp", 0)
            true
        } catch (e: Exception) {
            try {
                packageManager.getPackageInfo("com.whatsapp.w4b", 0)
                true
            } catch (e: Exception) {
                false
            }
        }
    }
}
