option.value:
package com.unasp.unaspmarketplace

import android.content.Intent
import android.os.Bundle
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import androidx.appcompat.app.AppCompatActivity
import android.widget.ImageView
import android.widget.Button
import android.widget.TextView
import android.widget.Toast
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.RecyclerView
import com.google.android.material.bottomnavigation.BottomNavigationView
import com.unasp.unaspmarketplace.utils.CartManager
import com.unasp.unaspmarketplace.utils.CartBadgeManager

class CartActivity : AppCompatActivity(), CartManager.CartUpdateListener {

    private lateinit var recyclerCart: RecyclerView
    private lateinit var txtTotal: TextView
    private lateinit var btnCheckout: Button
    private lateinit var cartAdapter: CartAdapter

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.cart_activity)

        initViews()
        setupRecyclerView()
        setupButtons()
        setupBottomNavigation()
        loadCartItems()

        // Registrar listener do carrinho
        CartManager.addListener(this)
    }

    override fun onDestroy() {
        super.onDestroy()
        CartManager.removeListener(this)
    }

    override fun onCartUpdated(itemCount: Int, totalPrice: Double) {
        updateUI()
        CartBadgeManager.updateBadge(itemCount)
    }

    private fun initViews() {
        txtTotal = findViewById(R.id.txtTotal)
        btnCheckout = findViewById(R.id.btnCheckout)
        recyclerCart = findViewById(R.id.recyclerCart)

        val btnBack = findViewById<ImageView>(R.id.btnBack)
        btnBack.setOnClickListener { finish() }

        val btnClearCart = findViewById<ImageView>(R.id.btnClearCart)
        btnClearCart.setOnClickListener {
            CartManager.clearCart()
            Toast.makeText(this, "Carrinho limpo!", Toast.LENGTH_SHORT).show()
        }
    }

    private fun setupRecyclerView() {
        cartAdapter = CartAdapter()
        recyclerCart.layoutManager = LinearLayoutManager(this)
        recyclerCart.adapter = cartAdapter
    }

    private fun setupButtons() {
        btnCheckout.setOnClickListener {
            val cartItems = CartManager.getCartItems()
            if (cartItems.isEmpty()) {
                Toast.makeText(this, "Carrinho vazio!", Toast.LENGTH_SHORT).show()
            } else {
                val total = CartManager.getTotalPrice()
                Toast.makeText(this, "Compra finalizada!\nTotal: R$ %.2f".format(total), Toast.LENGTH_LONG).show()
                CartManager.clearCart()
            }
        }
    }

    private fun loadCartItems() {
        updateUI()
    }

    private fun updateUI() {
        val cartItems = CartManager.getCartItems()
        cartAdapter.updateItems(cartItems)

        val total = CartManager.getTotalPrice()
        txtTotal.text = "Total: R$ %.2f".format(total)

        btnCheckout.isEnabled = cartItems.isNotEmpty()
        btnCheckout.alpha = if (cartItems.isNotEmpty()) 1.0f else 0.5f

        // Mostrar mensagem se carrinho vazio
        if (cartItems.isEmpty()) {
            Toast.makeText(this, "Carrinho vazio", Toast.LENGTH_SHORT).show()
        }
    }

    private fun setupBottomNavigation() {
        val bottomNavigation = findViewById<BottomNavigationView>(R.id.bottom_navigation_cart)
        bottomNavigation.selectedItemId = R.id.nav_cart

        CartBadgeManager.setupCartBadge(bottomNavigation)
        CartBadgeManager.updateBadge(CartManager.getTotalItemCount())

        bottomNavigation.setOnItemSelectedListener { item ->
            when (item.itemId) {
                R.id.nav_menu -> {
                    val intent = Intent(this, HomeActivity::class.java)
                    intent.putExtra("openMenu", true)
                    startActivity(intent)
                    finish()
                    true
                }
                R.id.nav_home -> {
                    val intent = Intent(this, HomeActivity::class.java)
                    startActivity(intent)
                    finish()
                    true
                }
                R.id.nav_notifications -> {
                    Toast.makeText(this, "Notificações em breve", Toast.LENGTH_SHORT).show()
                    true
                }
                R.id.nav_cart -> {
                    Toast.makeText(this, "Você já está no Carrinho", Toast.LENGTH_SHORT).show()
                    true
                }
                else -> false
            }
        }
    }

    inner class CartAdapter : RecyclerView.Adapter<CartAdapter.CartViewHolder>() {
        private var items = listOf<com.unasp.unaspmarketplace.utils.CartItem>()

        fun updateItems(newItems: List<com.unasp.unaspmarketplace.utils.CartItem>) {
            items = newItems
            notifyDataSetChanged()
        }

        inner class CartViewHolder(itemView: View) : RecyclerView.ViewHolder(itemView) {
            val txtName: TextView = itemView.findViewById(R.id.txtName)
            val txtPrice: TextView = itemView.findViewById(R.id.txtPrice)
            val txtQuantity: TextView = itemView.findViewById(R.id.txtQuantity)
            val btnIncrease: Button = itemView.findViewById(R.id.btnIncrease)
            val btnDecrease: Button = itemView.findViewById(R.id.btnDecrease)
            val btnRemove: Button = itemView.findViewById(R.id.btnRemove)
            val imgProduct: ImageView = itemView.findViewById(R.id.imgProduct)
        }

        override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): CartViewHolder {
            val view = LayoutInflater.from(parent.context)
                .inflate(R.layout.cart_item, parent, false)
            return CartViewHolder(view)
        }

        override fun onBindViewHolder(holder: CartViewHolder, position: Int) {
            val item = items[position]
            val product = item.product

            holder.txtName.text = product.name
            holder.txtPrice.text = "R$ %.2f".format(item.totalPrice)
            holder.txtQuantity.text = item.quantity.toString()
            holder.imgProduct.setImageResource(R.drawable.ic_launcher_background)

            // Botão incrementar (+)
            holder.btnIncrease.isEnabled = item.quantity < product.stock
            holder.btnIncrease.setOnClickListener {
                if (CartManager.updateQuantity(product.id, item.quantity + 1)) {
                    // Atualização bem-sucedida
                } else {
                    Toast.makeText(itemView.context, "Estoque insuficiente!", Toast.LENGTH_SHORT).show()
                }
            }

            // Botão decrementar (-)
            holder.btnDecrease.isEnabled = item.quantity > 1
            holder.btnDecrease.setOnClickListener {
                if (item.quantity > 1) {
                    CartManager.updateQuantity(product.id, item.quantity - 1)
                }
            }

            // Botão remover
            holder.btnRemove.setOnClickListener {
                CartManager.removeFromCart(product.id)
                Toast.makeText(itemView.context, "Item removido do carrinho", Toast.LENGTH_SHORT).show()
            }
        }

        override fun getItemCount(): Int = items.size
    }
}

package com.unasp.unaspmarketplace

import android.os.Bundle
import androidx.appcompat.app.AppCompatActivity
import androidx.recyclerview.widget.GridLayoutManager
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.RecyclerView
import com.unasp.unaspmarketplace.modelos.Category
import com.unasp.unaspmarketplace.modelos.CategoryAdapter
import com.unasp.unaspmarketplace.modelos.ProductAdapter
import android.content.Intent
import androidx.drawerlayout.widget.DrawerLayout
import android.widget.Toast
import androidx.lifecycle.lifecycleScope
import com.google.android.material.navigation.NavigationView
import com.google.android.material.bottomnavigation.BottomNavigationView
import androidx.core.view.GravityCompat
import com.unasp.unaspmarketplace.models.Product
import com.unasp.unaspmarketplace.repository.ProductRepository
import kotlinx.coroutines.launch

class HomeActivity : AppCompatActivity() {
    private lateinit var productRepository: ProductRepository
    private lateinit var productAdapter: ProductAdapter
    private lateinit var recyclerProducts: RecyclerView

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.home_activity)

        // Inicializar repositório
        productRepository = ProductRepository()

        setupCategories()
        setupProducts()
        setupNavigation()

        // Carregar produtos
        loadProducts()
    }

    private fun setupCategories() {
        val categorys = listOf(
            Category("Roupas", R.drawable.tshirt_logo),
            Category("Eletrônicos", R.drawable.computer_logo),
            Category("Alimentos", R.drawable.apple_logo),
            Category("Livros", R.drawable.book_logo)
        )

        val recyclerCategory = findViewById<RecyclerView>(R.id.recyclerCategorys)
        recyclerCategory.layoutManager =
            LinearLayoutManager(this, LinearLayoutManager.HORIZONTAL, false)
        recyclerCategory.adapter = CategoryAdapter(categorys)
    }

    private fun setupProducts() {
        recyclerProducts = findViewById(R.id.recyclerProducts)
        recyclerProducts.layoutManager = GridLayoutManager(this, 2)

        // Inicializar adapter com lista vazia
        productAdapter = ProductAdapter(mutableListOf())
        recyclerProducts.adapter = productAdapter
    }

    private fun setupNavigation() {
        // Configuração do menu lateral
        val drawerLayout = findViewById<DrawerLayout>(R.id.drawerLayout)
        val navigationView = findViewById<NavigationView>(R.id.navigationView)

        // Verificar se deve abrir o menu automaticamente
        if (intent.getBooleanExtra("openMenu", false)) {
            drawerLayout.openDrawer(GravityCompat.START)
        }

        // Configuração da hotbar inferior
        val bottomNavigation = findViewById<BottomNavigationView>(R.id.bottomNavigation)
        bottomNavigation.setOnItemSelectedListener { item ->
            when (item.itemId) {
                R.id.nav_menu -> {
                    // Abre o menu lateral
                    drawerLayout.openDrawer(GravityCompat.START)
                    true
                }
                R.id.nav_home -> {
                    // Já estamos na home, não precisa fazer nada
                    Toast.makeText(this, "Você já está na Home", Toast.LENGTH_SHORT).show()
                    true
                }
                R.id.nav_notifications -> {
                    // Implementar navegação para notificações
                    Toast.makeText(this, "Notificações em breve", Toast.LENGTH_SHORT).show()
                    true
                }
                R.id.nav_cart -> {
                    // Navegar para o carrinho
                    val intent = Intent(this, CartActivity::class.java)
                    startActivity(intent)
                    true
                }
                else -> false
            }
        }

        // Configuração do menu lateral
        navigationView.setNavigationItemSelectedListener { item ->
            when (item.itemId) {
                R.id.nav_post_item -> {
                    val intent = Intent(this, PostItemActivity::class.java)
                    startActivity(intent)
                }
                R.id.nav_profile -> {
                    Toast.makeText(this, "Perfil", Toast.LENGTH_SHORT).show()
                }
            }
            drawerLayout.closeDrawers() // fecha o menu depois do clique
            true
        }
    }

    private fun loadProducts() {
        lifecycleScope.launch {
            try {
                val result = productRepository.getActiveProducts()

                if (result.isSuccess) {
                    val products = result.getOrNull() ?: emptyList()
                    runOnUiThread {
                        productAdapter.updateProducts(products)

                        if (products.isEmpty()) {
                            Toast.makeText(this@HomeActivity, "Nenhum produto encontrado. Que tal publicar o primeiro?", Toast.LENGTH_LONG).show()
                        }
                    }
                } else {
                    val error = result.exceptionOrNull()?.message ?: "Erro desconhecido"
                    runOnUiThread {
                        Toast.makeText(this@HomeActivity, "Erro ao carregar produtos: $error", Toast.LENGTH_LONG).show()

                        // Carregar produtos de exemplo se houver erro
                        loadSampleProducts()
                    }
                }
            } catch (e: Exception) {
                runOnUiThread {
                    Toast.makeText(this@HomeActivity, "Erro de conexão: ${e.message}", Toast.LENGTH_LONG).show()
                    loadSampleProducts()
                }
            }
        }
    }

    private fun loadSampleProducts() {
        // Produtos de exemplo caso não consiga carregar do Firebase
        val sampleProducts = listOf(
            Product(
                name = "Notebook Dell",
                description = "Notebook Dell Inspiron",
                price = 3500.0,
                category = "Eletrônicos",
                stock = 5,
                isActive = true
            ),
            Product(
                name = "Camiseta Azul",
                description = "Camiseta Nike azul",
                price = 79.9,
                category = "Roupas",
                stock = 10,
                isActive = true
            ),
            Product(
                name = "Livro Kotlin",
                description = "Livro sobre programação Kotlin",
                price = 120.0,
                category = "Livros",
                stock = 3,
                isActive = true
            )
        )

        productAdapter.updateProducts(sampleProducts)
    }

    override fun onResume() {
        super.onResume()
        // Recarregar produtos quando voltar para a tela
        loadProducts()
    }
}

package com.unasp.unaspmarketplace

import android.content.Intent
import android.os.Bundle
import android.widget.ImageView
import android.widget.TextView
import android.widget.Toast
import androidx.appcompat.app.AppCompatActivity
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.RecyclerView
import com.bumptech.glide.Glide
import com.google.android.material.bottomnavigation.BottomNavigationView
import com.google.android.material.button.MaterialButton
import com.google.firebase.firestore.FirebaseFirestore
import androidx.lifecycle.lifecycleScope
import kotlinx.coroutines.launch
import kotlinx.coroutines.tasks.await
import com.unasp.unaspmarketplace.adapters.ProductDetailImageAdapter
import com.unasp.unaspmarketplace.models.Product
import com.unasp.unaspmarketplace.models.User
import com.unasp.unaspmarketplace.utils.CartManager
import com.unasp.unaspmarketplace.utils.CartBadgeManager
import com.unasp.unaspmarketplace.utils.WhatsAppHelper
import com.unasp.unaspmarketplace.utils.UserUtils

class ProductDetailActivity : AppCompatActivity(), CartManager.CartUpdateListener {
    private lateinit var product: Product
    private var currentQuantity = 1

    private lateinit var txtProductName: TextView
    private lateinit var txtProductCategory: TextView
    private lateinit var txtProductPrice: TextView
    private lateinit var txtProductStock: TextView
    private lateinit var txtProductDescription: TextView
    private lateinit var txtSellerName: TextView
    private lateinit var imgProductDetail: ImageView
    private lateinit var recyclerProductImages: RecyclerView
    private lateinit var txtQuantity: TextView
    private lateinit var btnDecrease: MaterialButton
    private lateinit var btnIncrease: MaterialButton
    private lateinit var btnAddToCart: MaterialButton
    private lateinit var btnContactSeller: MaterialButton

    private lateinit var imageAdapter: ProductDetailImageAdapter

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_product_detail)

        initViews()
        getProductFromIntent()
        setupQuantityControls()
        setupButtons()
        setupBottomNavigation()
        displayProductInfo()

        // Registrar listener do carrinho
        CartManager.addListener(this)
    }

    override fun onDestroy() {
        super.onDestroy()
        // Remover listener para evitar memory leaks
        CartManager.removeListener(this)
    }

    override fun onCartUpdated(itemCount: Int, totalPrice: Double) {
        // Atualizar badge do carrinho
        CartBadgeManager.updateBadge(itemCount)
    }

    private fun initViews() {
        txtProductName = findViewById(R.id.txtProductName)
        txtProductCategory = findViewById(R.id.txtProductCategory)
        txtProductPrice = findViewById(R.id.txtProductPrice)
        txtProductStock = findViewById(R.id.txtProductStock)
        txtProductDescription = findViewById(R.id.txtProductDescription)
        txtSellerName = findViewById(R.id.txtSellerName)
        imgProductDetail = findViewById(R.id.imgProductDetail)
        recyclerProductImages = findViewById(R.id.recyclerProductImages)
        txtQuantity = findViewById(R.id.txtQuantity)
        btnDecrease = findViewById(R.id.btnDecrease)
        btnIncrease = findViewById(R.id.btnIncrease)
        btnAddToCart = findViewById(R.id.btnAddToCart)
        btnContactSeller = findViewById(R.id.btnContactSeller)

        val btnBack = findViewById<ImageView>(R.id.btnBack)
        btnBack.setOnClickListener { finish() }

        // Configurar RecyclerView de imagens
        setupImageRecycler()
    }

    private fun setupImageRecycler() {
        imageAdapter = ProductDetailImageAdapter { imageUrl ->
            // Quando uma imagem é clicada, mostrar na ImageView principal
            Glide.with(this)
                .load(imageUrl)
                .placeholder(R.drawable.ic_launcher_background)
                .error(R.drawable.ic_launcher_background)
                .centerCrop()
                .into(imgProductDetail)
        }

        recyclerProductImages.apply {
            adapter = imageAdapter
            layoutManager = LinearLayoutManager(this@ProductDetailActivity, LinearLayoutManager.HORIZONTAL, false)
        }
    }

    private fun getProductFromIntent() {
        // Receber dados do produto via Intent
        val imageUrls = intent.getStringArrayExtra("productImageUrls")?.toList() ?: emptyList()

        product = Product(
            id = intent.getStringExtra("productId") ?: "",
            name = intent.getStringExtra("productName") ?: "",
            description = intent.getStringExtra("productDescription") ?: "",
            price = intent.getDoubleExtra("productPrice", 0.0),
            stock = intent.getIntExtra("productStock", 0),
            category = intent.getStringExtra("productCategory") ?: "",
            sellerId = intent.getStringExtra("productSellerId") ?: "",
            imageUrls = imageUrls,
            active = intent.getBooleanExtra("productActive", true)
        )
    }

    private fun displayProductInfo() {
        txtProductName.text = product.name
        txtProductCategory.text = product.category
        txtProductPrice.text = "R$ %.2f".format(product.price)
        txtProductStock.text = "${product.stock} unidades"
        txtProductDescription.text = product.description

        // Buscar nome real do vendedor
        loadSellerName()

        // Exibir imagens do produto
        if (product.imageUrls.isNotEmpty()) {
            // Mostrar primeira imagem na ImageView principal
            Glide.with(this)
                .load(product.imageUrls.first())
                .placeholder(R.drawable.ic_launcher_background)
                .error(R.drawable.ic_launcher_background)
                .centerCrop()
                .into(imgProductDetail)

            // Atualizar adapter com todas as imagens
            imageAdapter.updateImages(product.imageUrls)
        } else {
            // Usar imagem padrão se não houver imagens
            imgProductDetail.setImageResource(R.drawable.ic_launcher_background)
            imageAdapter.updateImages(emptyList())
        }

        // Atualizar botões baseado no estoque
        updateQuantityControls()
        updateAddToCartButton()
        updateContactSellerButton()
    }

    private fun setupQuantityControls() {
        btnDecrease.setOnClickListener {
            if (currentQuantity > 1) {
                currentQuantity--
                updateQuantityDisplay()
                updateQuantityControls()
            }
        }

        btnIncrease.setOnClickListener {
            if (currentQuantity < product.stock) {
                currentQuantity++
                updateQuantityDisplay()
                updateQuantityControls()
            }
        }

        updateQuantityDisplay()
    }

    private fun updateQuantityDisplay() {
        txtQuantity.text = currentQuantity.toString()
    }

    private fun updateQuantityControls() {
        btnDecrease.isEnabled = currentQuantity > 1
        btnIncrease.isEnabled = currentQuantity < product.stock

        // Visual feedback
        btnDecrease.alpha = if (currentQuantity > 1) 1.0f else 0.5f
        btnIncrease.alpha = if (currentQuantity < product.stock) 1.0f else 0.5f
    }

    private fun updateAddToCartButton() {
        btnAddToCart.isEnabled = product.stock > 0
        if (product.stock > 0) {
            btnAddToCart.text = "Adicionar ao Carrinho"
            btnAddToCart.alpha = 1.0f
        } else {
            btnAddToCart.text = "Produto Esgotado"
            btnAddToCart.alpha = 0.6f
        }
    }

    private fun updateContactSellerButton() {
        lifecycleScope.launch {
            try {
                val currentUserId = UserUtils.getCurrentUserId()

                if (currentUserId == product.sellerId) {
                    // É o próprio produto do usuário
                    runOnUiThread {
                        btnContactSeller.text = " Seu Produto"
                        btnContactSeller.isEnabled = false
                        btnContactSeller.alpha = 0.6f
                    }
                } else {
                    // Produto de outro usuário
                    runOnUiThread {
                        btnContactSeller.text = " Falar com o Vendedor"
                        btnContactSeller.isEnabled = true
                        btnContactSeller.alpha = 1.0f
                    }
                }
            } catch (e: Exception) {
                // Em caso de erro, manter o botão ativo
                runOnUiThread {
                    btnContactSeller.isEnabled = true
                    btnContactSeller.alpha = 1.0f
                }
            }
        }
    }

    private fun setupButtons() {
        btnAddToCart.setOnClickListener {
            if (product.stock >= currentQuantity) {
                addToCart()
            } else {
                Toast.makeText(this, "Estoque insuficiente!", Toast.LENGTH_SHORT).show()
            }
        }

        btnContactSeller.setOnClickListener {
            contactSeller()
        }
    }

    private fun addToCart() {
        val success = CartManager.addToCart(product, currentQuantity)

        if (success) {
            val totalItems = CartManager.getTotalItemCount()
            Toast.makeText(
                this,
                "Adicionado ao carrinho!\n${product.name}\nQuantidade: $currentQuantity",
                Toast.LENGTH_LONG
            ).show()

            // Forçar atualização do badge
            CartBadgeManager.updateBadge(totalItems)
        } else {
            Toast.makeText(
                this,
                "Erro: Estoque insuficiente!\nDisponível: ${product.stock} unidades",
                Toast.LENGTH_LONG
            ).show()
        }
    }

    private fun setupBottomNavigation() {
        val bottomNavigation = findViewById<BottomNavigationView>(R.id.bottom_navigation_product)

        // Configurar badge do carrinho
        CartBadgeManager.setupCartBadge(bottomNavigation)
        CartBadgeManager.updateBadge(CartManager.getTotalItemCount())

        bottomNavigation.setOnItemSelectedListener { item ->
            when (item.itemId) {
                R.id.nav_menu -> {
                    val intent = Intent(this, HomeActivity::class.java)
                    intent.putExtra("openMenu", true)
                    startActivity(intent)
                    finish()
                    true
                }
                R.id.nav_home -> {
                    val intent = Intent(this, HomeActivity::class.java)
                    startActivity(intent)
                    finish()
                    true
                }
                R.id.nav_profile -> {
                    val intent = Intent(this, ProfileActivity::class.java)
                    startActivity(intent)
                    finish()
                    true
                }
                R.id.nav_cart -> {
                    val intent = Intent(this, CartActivity::class.java)
                    startActivity(intent)
                    finish()
                    true
                }
                else -> false
            }
        }
    }

    /**
     * Busca o nome real do vendedor pelo sellerId
     */
    private fun loadSellerName() {
        if (product.sellerId.isEmpty()) {
            txtSellerName.text = "Vendedor: Não informado"
            return
        }

        lifecycleScope.launch {
            try {
                val firestore = FirebaseFirestore.getInstance()
                val userDoc = firestore.collection("users")
                    .document(product.sellerId)
                    .get()
                    .await()

                if (userDoc.exists()) {
                    val user = userDoc.toObject(User::class.java)
                    val sellerName = user?.name ?: "Nome não disponível"
                    runOnUiThread {
                        txtSellerName.text = "Vendedor: $sellerName"
                    }
                } else {
                    runOnUiThread {
                        txtSellerName.text = "Vendedor: Usuário não encontrado"
                    }
                }
            } catch (e: Exception) {
                android.util.Log.e("ProductDetail", "Erro ao buscar vendedor", e)
                runOnUiThread {
                    txtSellerName.text = "Vendedor: Erro ao carregar"
                }
            }
        }
    }

    private fun contactSeller() {
        lifecycleScope.launch {
            try {
                // Buscar dados do vendedor
                val sellerDoc = FirebaseFirestore.getInstance()
                    .collection("users")
                    .document(product.sellerId)
                    .get()
                    .await()

                val seller = sellerDoc.toObject(User::class.java)

                if (seller != null && seller.whatsappNumber.isNotBlank()) {
                    // Buscar dados do comprador atual
                    val currentUser = UserUtils.getCurrentUser()
                    val buyerName = currentUser?.name ?: "Comprador"

                    // Criar mensagem personalizada
                    val message = formatContactMessage(seller.name, buyerName)

                    runOnUiThread {
                        Toast.makeText(this@ProductDetailActivity, "Abrindo WhatsApp...", Toast.LENGTH_SHORT).show()
                        WhatsAppHelper.sendMessage(this@ProductDetailActivity, message, seller.whatsappNumber)
                    }
                } else {
                    runOnUiThread {
                        Toast.makeText(
                            this@ProductDetailActivity,
                            "Vendedor não possui WhatsApp cadastrado. Adicione o produto ao carrinho e finalize a compra.",
                            Toast.LENGTH_LONG
                        ).show()
                    }
                }
            } catch (e: Exception) {
                android.util.Log.e("ProductDetail", "Erro ao contatar vendedor", e)
                runOnUiThread {
                    Toast.makeText(
                        this@ProductDetailActivity,
                        "Erro ao buscar dados do vendedor: ${e.message}",
                        Toast.LENGTH_LONG
                    ).show()
                }
            }
        }
    }

    private fun formatContactMessage(sellerName: String, buyerName: String): String {
        return """
 Olá, $sellerName!

Eu sou $buyerName e tenho interesse no seu produto:

 *${product.name}*
 R$ ${String.format("%.2f", product.price)}
 Categoria: ${product.category}

Gostaria de saber mais detalhes sobre o produto. Você pode me ajudar?

_Mensagem enviada através do UNASP Marketplace_
        """.trimIndent()
    }
}

package com.unasp.unaspmarketplace

import android.content.Intent
import android.os.Bundle
import android.widget.ImageView
import android.widget.TextView
import android.widget.Toast
import androidx.appcompat.app.AppCompatActivity
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.RecyclerView
import com.bumptech.glide.Glide
import com.google.android.material.bottomnavigation.BottomNavigationView
import com.google.android.material.button.MaterialButton
import com.google.firebase.firestore.FirebaseFirestore
import androidx.lifecycle.lifecycleScope
import kotlinx.coroutines.launch
import kotlinx.coroutines.tasks.await
import com.unasp.unaspmarketplace.adapters.ProductDetailImageAdapter
import com.unasp.unaspmarketplace.models.Product
import com.unasp.unaspmarketplace.models.User
import com.unasp.unaspmarketplace.utils.CartManager
import com.unasp.unaspmarketplace.utils.CartBadgeManager
import com.unasp.unaspmarketplace.utils.WhatsAppHelper
import com.unasp.unaspmarketplace.utils.UserUtils

class ProductDetailActivity : AppCompatActivity(), CartManager.CartUpdateListener {
    private lateinit var product: Product
    private var currentQuantity = 1

    private lateinit var txtProductName: TextView
    private lateinit var txtProductCategory: TextView
    private lateinit var txtProductPrice: TextView
    private lateinit var txtProductStock: TextView
    private lateinit var txtProductDescription: TextView
    private lateinit var txtSellerName: TextView
    private lateinit var imgProductDetail: ImageView
    private lateinit var recyclerProductImages: RecyclerView
    private lateinit var txtQuantity: TextView
    private lateinit var btnDecrease: MaterialButton
    private lateinit var btnIncrease: MaterialButton
    private lateinit var btnAddToCart: MaterialButton
    private lateinit var btnContactSeller: MaterialButton

    private lateinit var imageAdapter: ProductDetailImageAdapter

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_product_detail)

        initViews()
        getProductFromIntent()
        setupQuantityControls()
        setupButtons()
        setupBottomNavigation()
        displayProductInfo()

        // Registrar listener do carrinho
        CartManager.addListener(this)
    }

    override fun onDestroy() {
        super.onDestroy()
        // Remover listener para evitar memory leaks
        CartManager.removeListener(this)
    }

    override fun onCartUpdated(itemCount: Int, totalPrice: Double) {
        // Atualizar badge do carrinho
        CartBadgeManager.updateBadge(itemCount)
    }

    private fun initViews() {
        txtProductName = findViewById(R.id.txtProductName)
        txtProductCategory = findViewById(R.id.txtProductCategory)
        txtProductPrice = findViewById(R.id.txtProductPrice)
        txtProductStock = findViewById(R.id.txtProductStock)
        txtProductDescription = findViewById(R.id.txtProductDescription)
        txtSellerName = findViewById(R.id.txtSellerName)
        imgProductDetail = findViewById(R.id.imgProductDetail)
        recyclerProductImages = findViewById(R.id.recyclerProductImages)
        txtQuantity = findViewById(R.id.txtQuantity)
        btnDecrease = findViewById(R.id.btnDecrease)
        btnIncrease = findViewById(R.id.btnIncrease)
        btnAddToCart = findViewById(R.id.btnAddToCart)
        btnContactSeller = findViewById(R.id.btnContactSeller)

        // Configurar botão de voltar no toolbar
        val toolbar = findViewById<com.google.android.material.appbar.MaterialToolbar>(R.id.toolbarProduct)
        toolbar.setNavigationOnClickListener { finish() }

        // Configurar RecyclerView de imagens
        setupImageRecycler()
    }

    private fun setupImageRecycler() {
        imageAdapter = ProductDetailImageAdapter { imageUrl ->
            // Quando uma imagem é clicada, mostrar na ImageView principal
            Glide.with(this)
                .load(imageUrl)
                .placeholder(R.drawable.ic_launcher_background)
                .error(R.drawable.ic_launcher_background)
                .centerCrop()
                .into(imgProductDetail)
        }

        recyclerProductImages.apply {
            adapter = imageAdapter
            layoutManager = LinearLayoutManager(this@ProductDetailActivity, LinearLayoutManager.HORIZONTAL, false)
        }
    }

    private fun getProductFromIntent() {
        // Receber dados do produto via Intent
        val imageUrls = intent.getStringArrayExtra("productImageUrls")?.toList() ?: emptyList()

        product = Product(
            id = intent.getStringExtra("productId") ?: "",
            name = intent.getStringExtra("productName") ?: "",
            description = intent.getStringExtra("productDescription") ?: "",
            price = intent.getDoubleExtra("productPrice", 0.0),
            stock = intent.getIntExtra("productStock", 0),
            category = intent.getStringExtra("productCategory") ?: "",
            sellerId = intent.getStringExtra("productSellerId") ?: "",
            imageUrls = imageUrls,
            active = intent.getBooleanExtra("productActive", true)
        )
    }

    private fun displayProductInfo() {
        txtProductName.text = product.name
        txtProductCategory.text = product.category
        txtProductPrice.text = "R$ %.2f".format(product.price)
        txtProductStock.text = "${product.stock} unidades"
        txtProductDescription.text = product.description

        // Buscar nome real do vendedor
        loadSellerName()

        // Exibir imagens do produto
        if (product.imageUrls.isNotEmpty()) {
            // Mostrar primeira imagem na ImageView principal
            Glide.with(this)
                .load(product.imageUrls.first())
                .placeholder(R.drawable.ic_launcher_background)
                .error(R.drawable.ic_launcher_background)
                .centerCrop()
                .into(imgProductDetail)

            // Atualizar adapter com todas as imagens
            imageAdapter.updateImages(product.imageUrls)
        } else {
            // Usar imagem padrão se não houver imagens
            imgProductDetail.setImageResource(R.drawable.ic_launcher_background)
            imageAdapter.updateImages(emptyList())
        }

        // Atualizar botões baseado no estoque
        updateQuantityControls()
        updateAddToCartButton()
        updateContactSellerButton()
    }

    private fun setupQuantityControls() {
        btnDecrease.setOnClickListener {
            if (currentQuantity > 1) {
                currentQuantity--
                updateQuantityDisplay()
                updateQuantityControls()
            }
        }

        btnIncrease.setOnClickListener {
            if (currentQuantity < product.stock) {
                currentQuantity++
                updateQuantityDisplay()
                updateQuantityControls()
            }
        }

        updateQuantityDisplay()
    }

    private fun updateQuantityDisplay() {
        txtQuantity.text = currentQuantity.toString()
    }

    private fun updateQuantityControls() {
        btnDecrease.isEnabled = currentQuantity > 1
        btnIncrease.isEnabled = currentQuantity < product.stock

        // Visual feedback
        btnDecrease.alpha = if (currentQuantity > 1) 1.0f else 0.5f
        btnIncrease.alpha = if (currentQuantity < product.stock) 1.0f else 0.5f
    }

    private fun updateAddToCartButton() {
        btnAddToCart.isEnabled = product.stock > 0
        if (product.stock > 0) {
            btnAddToCart.text = "Adicionar ao Carrinho"
            btnAddToCart.alpha = 1.0f
        } else {
            btnAddToCart.text = "Produto Esgotado"
            btnAddToCart.alpha = 0.6f
        }
    }

    private fun updateContactSellerButton() {
        lifecycleScope.launch {
            try {
                val currentUserId = UserUtils.getCurrentUserId()

                if (currentUserId == product.sellerId) {
                    // É o próprio produto do usuário
                    runOnUiThread {
                        btnContactSeller.text = " Seu Produto"
                        btnContactSeller.isEnabled = false
                        btnContactSeller.alpha = 0.6f
                    }
                } else {
                    // Produto de outro usuário
                    runOnUiThread {
                        btnContactSeller.text = " Falar com o Vendedor"
                        btnContactSeller.isEnabled = true
                        btnContactSeller.alpha = 1.0f
                    }
                }
            } catch (e: Exception) {
                // Em caso de erro, manter o botão ativo
                runOnUiThread {
                    btnContactSeller.isEnabled = true
                    btnContactSeller.alpha = 1.0f
                }
            }
        }
    }

    private fun setupButtons() {
        btnAddToCart.setOnClickListener {
            if (product.stock >= currentQuantity) {
                addToCart()
            } else {
                Toast.makeText(this, "Estoque insuficiente!", Toast.LENGTH_SHORT).show()
            }
        }

        btnContactSeller.setOnClickListener {
            contactSeller()
        }
    }

    private fun addToCart() {
        val success = CartManager.addToCart(product, currentQuantity)

        if (success) {
            val totalItems = CartManager.getTotalItemCount()
            Toast.makeText(
                this,
                "Adicionado ao carrinho!\n${product.name}\nQuantidade: $currentQuantity",
                Toast.LENGTH_LONG
            ).show()

            // Forçar atualização do badge
            CartBadgeManager.updateBadge(totalItems)
        } else {
            Toast.makeText(
                this,
                "Erro: Estoque insuficiente!\nDisponível: ${product.stock} unidades",
                Toast.LENGTH_LONG
            ).show()
        }
    }

    private fun setupBottomNavigation() {
        val bottomNavigation = findViewById<BottomNavigationView>(R.id.bottom_navigation_product)

        // Configurar badge do carrinho
        CartBadgeManager.setupCartBadge(bottomNavigation)
        CartBadgeManager.updateBadge(CartManager.getTotalItemCount())

        bottomNavigation.setOnItemSelectedListener { item ->
            when (item.itemId) {
                R.id.nav_menu -> {
                    val intent = Intent(this, HomeActivity::class.java)
                    intent.putExtra("openMenu", true)
                    startActivity(intent)
                    finish()
                    true
                }
                R.id.nav_home -> {
                    val intent = Intent(this, HomeActivity::class.java)
                    startActivity(intent)
                    finish()
                    true
                }
                R.id.nav_profile -> {
                    val intent = Intent(this, ProfileActivity::class.java)
                    startActivity(intent)
                    finish()
                    true
                }
                R.id.nav_cart -> {
                    val intent = Intent(this, CartActivity::class.java)
                    startActivity(intent)
                    finish()
                    true
                }
                else -> false
            }
        }
    }

    /**
     * Busca o nome real do vendedor pelo sellerId
     */
    private fun loadSellerName() {
        if (product.sellerId.isEmpty()) {
            txtSellerName.text = "Vendedor: Não informado"
            return
        }

        lifecycleScope.launch {
            try {
                val firestore = FirebaseFirestore.getInstance()
                val userDoc = firestore.collection("users")
                    .document(product.sellerId)
                    .get()
                    .await()

                if (userDoc.exists()) {
                    val user = userDoc.toObject(User::class.java)
                    val sellerName = user?.name ?: "Nome não disponível"
                    runOnUiThread {
                        txtSellerName.text = "Vendedor: $sellerName"
                    }
                } else {
                    runOnUiThread {
                        txtSellerName.text = "Vendedor: Usuário não encontrado"
                    }
                }
            } catch (e: Exception) {
                android.util.Log.e("ProductDetail", "Erro ao buscar vendedor", e)
                runOnUiThread {
                    txtSellerName.text = "Vendedor: Erro ao carregar"
                }
            }
        }
    }

    private fun contactSeller() {
        lifecycleScope.launch {
            try {
                // Buscar dados do vendedor
                val sellerDoc = FirebaseFirestore.getInstance()
                    .collection("users")
                    .document(product.sellerId)
                    .get()
                    .await()

                val seller = sellerDoc.toObject(User::class.java)

                if (seller != null && seller.whatsappNumber.isNotBlank()) {
                    // Buscar dados do comprador atual
                    val currentUser = UserUtils.getCurrentUser()
                    val buyerName = currentUser?.name ?: "Comprador"

                    // Criar mensagem personalizada
                    val message = formatContactMessage(seller.name, buyerName)

                    runOnUiThread {
                        Toast.makeText(this@ProductDetailActivity, "Abrindo WhatsApp...", Toast.LENGTH_SHORT).show()
                        WhatsAppHelper.sendMessage(this@ProductDetailActivity, message, seller.whatsappNumber)
                    }
                } else {
                    runOnUiThread {
                        Toast.makeText(
                            this@ProductDetailActivity,
                            "Vendedor não possui WhatsApp cadastrado. Adicione o produto ao carrinho e finalize a compra.",
                            Toast.LENGTH_LONG
                        ).show()
                    }
                }
            } catch (e: Exception) {
                android.util.Log.e("ProductDetail", "Erro ao contatar vendedor", e)
                runOnUiThread {
                    Toast.makeText(
                        this@ProductDetailActivity,
                        "Erro ao buscar dados do vendedor: ${e.message}",
                        Toast.LENGTH_LONG
                    ).show()
                }
            }
        }
    }

    private fun formatContactMessage(sellerName: String, buyerName: String): String {
        return """
 Olá, $sellerName!

Eu sou $buyerName e tenho interesse no seu produto:

 *${product.name}*
 R$ ${String.format("%.2f", product.price)}
 Categoria: ${product.category}

Gostaria de saber mais detalhes sobre o produto. Você pode me ajudar?

_Mensagem enviada através do UNASP Marketplace_
        """.trimIndent()
    }
}

